&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;    &amp;lt;!&lt;span class=&quot;hljs-variable constant_&quot;&gt;DOCTYPE&lt;/span&gt; html&amp;gt;
    &lt;span class=&quot;language-xml&quot;&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;html&lt;/span&gt;&amp;gt;&lt;/span&gt;

    &lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;head&lt;/span&gt;&amp;gt;&lt;/span&gt;
        &lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;style&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;span class=&quot;language-css&quot;&gt;
            &lt;span class=&quot;hljs-comment&quot;&gt;/*  &amp;lt;span class=&quot;metadata-marker&quot; style=&quot;display: none;&quot; data-region_tag=&quot;css&quot;&amp;gt;&amp;lt;/span&amp;gt;       Set the size of the div element that contains the map */&lt;/span&gt;
            &lt;span class=&quot;hljs-selector-id&quot;&gt;#map&lt;/span&gt; {
                &lt;span class=&quot;hljs-attribute&quot;&gt;height&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;400px&lt;/span&gt;;
                &lt;span class=&quot;hljs-comment&quot;&gt;/* The height is 400 pixels */&lt;/span&gt;
                &lt;span class=&quot;hljs-attribute&quot;&gt;width&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;100%&lt;/span&gt;;
                &lt;span class=&quot;hljs-comment&quot;&gt;/* The width is the width of the web page */&lt;/span&gt;
            }
        &lt;/span&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;style&lt;/span&gt;&amp;gt;&lt;/span&gt;
        &lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;script&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;span class=&quot;language-javascript&quot;&gt;
            &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; map;
            &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;InforObj&lt;/span&gt; = [];
            &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; centerCords = {
                &lt;span class=&quot;hljs-attr&quot;&gt;lat&lt;/span&gt;: -&lt;span class=&quot;hljs-number&quot;&gt;25.344&lt;/span&gt;,
                &lt;span class=&quot;hljs-attr&quot;&gt;lng&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;131.036&lt;/span&gt;
            };
            &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; markersOnMap = [{
                    &lt;span class=&quot;hljs-attr&quot;&gt;placeName&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;Australia (Uluru)&quot;&lt;/span&gt;,
                    &lt;span class=&quot;hljs-title class_&quot;&gt;LatLng&lt;/span&gt;: [{
                        &lt;span class=&quot;hljs-attr&quot;&gt;lat&lt;/span&gt;: -&lt;span class=&quot;hljs-number&quot;&gt;25.344&lt;/span&gt;,
                        &lt;span class=&quot;hljs-attr&quot;&gt;lng&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;131.036&lt;/span&gt;
                    }]
                },
                {
                    &lt;span class=&quot;hljs-attr&quot;&gt;placeName&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;Australia (Melbourne)&quot;&lt;/span&gt;,
                    &lt;span class=&quot;hljs-title class_&quot;&gt;LatLng&lt;/span&gt;: [{
                        &lt;span class=&quot;hljs-attr&quot;&gt;lat&lt;/span&gt;: -&lt;span class=&quot;hljs-number&quot;&gt;37.852086&lt;/span&gt;,
                        &lt;span class=&quot;hljs-attr&quot;&gt;lng&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;504.985963&lt;/span&gt;
                    }]
                },
                {
                    &lt;span class=&quot;hljs-attr&quot;&gt;placeName&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;Australia (Canberra)&quot;&lt;/span&gt;,
                    &lt;span class=&quot;hljs-title class_&quot;&gt;LatLng&lt;/span&gt;: [{
                        &lt;span class=&quot;hljs-attr&quot;&gt;lat&lt;/span&gt;: -&lt;span class=&quot;hljs-number&quot;&gt;35.299085&lt;/span&gt;,
                        &lt;span class=&quot;hljs-attr&quot;&gt;lng&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;509.109615&lt;/span&gt;
                    }]
                },
                {
                    &lt;span class=&quot;hljs-attr&quot;&gt;placeName&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;Australia (Gold Coast)&quot;&lt;/span&gt;,
                    &lt;span class=&quot;hljs-title class_&quot;&gt;LatLng&lt;/span&gt;: [{
                        &lt;span class=&quot;hljs-attr&quot;&gt;lat&lt;/span&gt;: -&lt;span class=&quot;hljs-number&quot;&gt;28.013044&lt;/span&gt;,
                        &lt;span class=&quot;hljs-attr&quot;&gt;lng&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;513.425586&lt;/span&gt;
                    }]
                },
                {
                    &lt;span class=&quot;hljs-attr&quot;&gt;placeName&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;Australia (Perth)&quot;&lt;/span&gt;,
                    &lt;span class=&quot;hljs-title class_&quot;&gt;LatLng&lt;/span&gt;: [{
                        &lt;span class=&quot;hljs-attr&quot;&gt;lat&lt;/span&gt;: -&lt;span class=&quot;hljs-number&quot;&gt;31.951994&lt;/span&gt;,
                        &lt;span class=&quot;hljs-attr&quot;&gt;lng&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;475.858081&lt;/span&gt;
                    }]
                }
            ];

            &lt;span class=&quot;hljs-variable language_&quot;&gt;window&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;onload&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
                &lt;span class=&quot;hljs-title function_&quot;&gt;initMap&lt;/span&gt;();
            };

            &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;addMarkerInfo&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
                &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; i = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; i &amp;lt; markersOnMap.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt;; i++) {
                    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; contentString = &lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;lt;div id=&quot;content&quot;&amp;gt;&amp;lt;h1&amp;gt;&apos;&lt;/span&gt; + markersOnMap[i].&lt;span class=&quot;hljs-property&quot;&gt;placeName&lt;/span&gt; +
                        &lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;lt;/h1&amp;gt;&amp;lt;p&amp;gt;Lorem ipsum dolor sit amet, vix mutat posse suscipit id, vel ea tantas omittam detraxit.&amp;lt;/p&amp;gt;&amp;lt;/div&amp;gt;&apos;&lt;/span&gt;;

                    &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; marker = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; google.&lt;span class=&quot;hljs-property&quot;&gt;maps&lt;/span&gt;.&lt;span class=&quot;hljs-title class_&quot;&gt;Marker&lt;/span&gt;({
                        &lt;span class=&quot;hljs-attr&quot;&gt;position&lt;/span&gt;: markersOnMap[i].&lt;span class=&quot;hljs-property&quot;&gt;LatLng&lt;/span&gt;[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;],
                        &lt;span class=&quot;hljs-attr&quot;&gt;map&lt;/span&gt;: map
                    });

                    &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; infowindow = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; google.&lt;span class=&quot;hljs-property&quot;&gt;maps&lt;/span&gt;.&lt;span class=&quot;hljs-title class_&quot;&gt;InfoWindow&lt;/span&gt;({
                        &lt;span class=&quot;hljs-attr&quot;&gt;content&lt;/span&gt;: contentString,
                        &lt;span class=&quot;hljs-attr&quot;&gt;maxWidth&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;200&lt;/span&gt;
                    });

                    marker.&lt;span class=&quot;hljs-title function_&quot;&gt;addListener&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;click&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
                        &lt;span class=&quot;hljs-title function_&quot;&gt;closeOtherInfo&lt;/span&gt;();
                        infowindow.&lt;span class=&quot;hljs-title function_&quot;&gt;open&lt;/span&gt;(marker.&lt;span class=&quot;hljs-title function_&quot;&gt;get&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;map&apos;&lt;/span&gt;), marker);
                        &lt;span class=&quot;hljs-title class_&quot;&gt;InforObj&lt;/span&gt;[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;] = infowindow;
                    });
                    &lt;span class=&quot;hljs-comment&quot;&gt;// marker.addListener(&apos;mouseover&apos;, function () {&lt;/span&gt;
                    &lt;span class=&quot;hljs-comment&quot;&gt;//     closeOtherInfo();&lt;/span&gt;
                    &lt;span class=&quot;hljs-comment&quot;&gt;//     infowindow.open(marker.get(&apos;map&apos;), marker);&lt;/span&gt;
                    &lt;span class=&quot;hljs-comment&quot;&gt;//     InforObj[0] = infowindow;&lt;/span&gt;
                    &lt;span class=&quot;hljs-comment&quot;&gt;// });&lt;/span&gt;
                    &lt;span class=&quot;hljs-comment&quot;&gt;// marker.addListener(&apos;mouseout&apos;, function () {&lt;/span&gt;
                    &lt;span class=&quot;hljs-comment&quot;&gt;//     closeOtherInfo();&lt;/span&gt;
                    &lt;span class=&quot;hljs-comment&quot;&gt;//     infowindow.close();&lt;/span&gt;
                    &lt;span class=&quot;hljs-comment&quot;&gt;//     InforObj[0] = infowindow;&lt;/span&gt;
                    &lt;span class=&quot;hljs-comment&quot;&gt;// });&lt;/span&gt;
                }
            }

            &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;closeOtherInfo&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
                &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-title class_&quot;&gt;InforObj&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;) {
                    &lt;span class=&quot;hljs-comment&quot;&gt;/* detach the info-window from the marker ... undocumented in the API docs */&lt;/span&gt;
                    &lt;span class=&quot;hljs-title class_&quot;&gt;InforObj&lt;/span&gt;[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;].&lt;span class=&quot;hljs-title function_&quot;&gt;set&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;marker&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;);
                    &lt;span class=&quot;hljs-comment&quot;&gt;/* and close it */&lt;/span&gt;
                    &lt;span class=&quot;hljs-title class_&quot;&gt;InforObj&lt;/span&gt;[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;].&lt;span class=&quot;hljs-title function_&quot;&gt;close&lt;/span&gt;();
                    &lt;span class=&quot;hljs-comment&quot;&gt;/* blank the array */&lt;/span&gt;
                    &lt;span class=&quot;hljs-title class_&quot;&gt;InforObj&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;
                }
            }

            &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;initMap&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
                map = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; google.&lt;span class=&quot;hljs-property&quot;&gt;maps&lt;/span&gt;.&lt;span class=&quot;hljs-title class_&quot;&gt;Map&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;getElementById&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;map&apos;&lt;/span&gt;), {
                    &lt;span class=&quot;hljs-attr&quot;&gt;zoom&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;,
                    &lt;span class=&quot;hljs-attr&quot;&gt;center&lt;/span&gt;: centerCords
                });
                &lt;span class=&quot;hljs-title function_&quot;&gt;addMarkerInfo&lt;/span&gt;();
            }
        &lt;/span&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;script&lt;/span&gt;&amp;gt;&lt;/span&gt;
    &lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;head&lt;/span&gt;&amp;gt;&lt;/span&gt;

    &lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;body&lt;/span&gt;&amp;gt;&lt;/span&gt;
        &lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;h3&lt;/span&gt;&amp;gt;&lt;/span&gt;My Google Maps Demo&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;h3&lt;/span&gt;&amp;gt;&lt;/span&gt;
        &lt;span class=&quot;hljs-comment&quot;&gt;&amp;lt;!--The div element for the map --&amp;gt;&lt;/span&gt;
        &lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;div&lt;/span&gt; &lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&quot;map&quot;&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;div&lt;/span&gt;&amp;gt;&lt;/span&gt;

        &lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;script&lt;/span&gt; &lt;span class=&quot;hljs-attr&quot;&gt;src&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&quot;https://maps.googleapis.com/maps/api/js?key=YOUR_API_KEY&quot;&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;script&lt;/span&gt;&amp;gt;&lt;/span&gt;

    &lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;body&lt;/span&gt;&amp;gt;&lt;/span&gt;

    &lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;html&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Following from &lt;a href=&quot;https://stackoverflow.com/a/3059129/1071884&quot;&gt;Daniel Vassallo&apos;s answer&lt;/a&gt;, here is a version that deals with the closure issue in a simpler way.&lt;/p&gt;

&lt;p&gt;Since since all markers will have an individual &lt;em&gt;InfoWindow&lt;/em&gt; and since JavaScript doesn&apos;t care if you add extra properties to an object, all you need to do is add an &lt;em&gt;InfoWindow&lt;/em&gt; to the &lt;em&gt;Marker&apos;s&lt;/em&gt; properties and then call the &lt;code&gt;.open()&lt;/code&gt; on the &lt;em&gt;InfoWindow&lt;/em&gt; from itself! &lt;/p&gt;

&lt;p&gt;&lt;strong&gt;Edit:&lt;/strong&gt; With enough data, the pageload could take a lot of time, so rather than construction the &lt;em&gt;InfoWindow&lt;/em&gt; with the marker, the construction should happen only when needed.  Note that any data used to construct the &lt;em&gt;InfoWindow&lt;/em&gt; must be appended to the &lt;em&gt;Marker&lt;/em&gt; as a property (&lt;code&gt;data&lt;/code&gt;). Also note that after the first click event, &lt;code&gt;infoWindow&lt;/code&gt; will persist as a property of it&apos;s marker so the browser doesn&apos;t need to constantly reconstruct.&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; locations = [
  [&lt;span class=&quot;hljs-string&quot;&gt;&apos;Bondi Beach&apos;&lt;/span&gt;, -&lt;span class=&quot;hljs-number&quot;&gt;33.890542&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;151.274856&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;],
  [&lt;span class=&quot;hljs-string&quot;&gt;&apos;Coogee Beach&apos;&lt;/span&gt;, -&lt;span class=&quot;hljs-number&quot;&gt;33.923036&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;151.259052&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;],
  [&lt;span class=&quot;hljs-string&quot;&gt;&apos;Cronulla Beach&apos;&lt;/span&gt;, -&lt;span class=&quot;hljs-number&quot;&gt;34.028249&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;151.157507&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;],
  [&lt;span class=&quot;hljs-string&quot;&gt;&apos;Manly Beach&apos;&lt;/span&gt;, -&lt;span class=&quot;hljs-number&quot;&gt;33.80010128657071&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;151.28747820854187&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;],
  [&lt;span class=&quot;hljs-string&quot;&gt;&apos;Maroubra Beach&apos;&lt;/span&gt;, -&lt;span class=&quot;hljs-number&quot;&gt;33.950198&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;151.259302&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;]
];

&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; map = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; google.&lt;span class=&quot;hljs-property&quot;&gt;maps&lt;/span&gt;.&lt;span class=&quot;hljs-title class_&quot;&gt;Map&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;getElementById&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;map&apos;&lt;/span&gt;), {
  &lt;span class=&quot;hljs-attr&quot;&gt;center&lt;/span&gt;: &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; google.&lt;span class=&quot;hljs-property&quot;&gt;maps&lt;/span&gt;.&lt;span class=&quot;hljs-title class_&quot;&gt;LatLng&lt;/span&gt;(-&lt;span class=&quot;hljs-number&quot;&gt;33.92&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;151.25&lt;/span&gt;)
});

&lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (i = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; i &amp;lt; locations.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt;; i++) {  
  marker = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; google.&lt;span class=&quot;hljs-property&quot;&gt;maps&lt;/span&gt;.&lt;span class=&quot;hljs-title class_&quot;&gt;Marker&lt;/span&gt;({
    &lt;span class=&quot;hljs-attr&quot;&gt;position&lt;/span&gt;: &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; google.&lt;span class=&quot;hljs-property&quot;&gt;maps&lt;/span&gt;.&lt;span class=&quot;hljs-title class_&quot;&gt;LatLng&lt;/span&gt;(locations[i][&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;], locations[i][&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;]),
    &lt;span class=&quot;hljs-attr&quot;&gt;map&lt;/span&gt;: map,
    &lt;span class=&quot;hljs-attr&quot;&gt;data&lt;/span&gt;: {
      &lt;span class=&quot;hljs-attr&quot;&gt;name&lt;/span&gt;: locations[i][&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]
    }
  });
  marker.&lt;span class=&quot;hljs-title function_&quot;&gt;addListener&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;click&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(!&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;infoWindow&lt;/span&gt;) {
      &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;infoWindow&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; google.&lt;span class=&quot;hljs-property&quot;&gt;maps&lt;/span&gt;.&lt;span class=&quot;hljs-title class_&quot;&gt;InfoWindow&lt;/span&gt;({
        &lt;span class=&quot;hljs-attr&quot;&gt;content&lt;/span&gt;: &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;data&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;name&lt;/span&gt;;
      });
    }
    &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;infoWindow&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;open&lt;/span&gt;(map,&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;);
  })
}
