    { &lt;span class=&quot;hljs-attr&quot;&gt;lat&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;42.03&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;lng&lt;/span&gt;: -&lt;span class=&quot;hljs-number&quot;&gt;77.02&lt;/span&gt; },
    { &lt;span class=&quot;hljs-attr&quot;&gt;lat&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;41.03&lt;/span&gt;, &lt;span class=&quot;hljs-attr&quot;&gt;lng&lt;/span&gt;: -&lt;span class=&quot;hljs-number&quot;&gt;77.04&lt;/span&gt; },
