  ]
  &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;onMarkerClick&lt;/span&gt; = (&lt;span class=&quot;hljs-params&quot;&gt;newProps, marker&lt;/span&gt;) =&amp;gt; {};

  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (!props.&lt;span class=&quot;hljs-property&quot;&gt;google&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;language-xml&quot;&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;div&lt;/span&gt;&amp;gt;&lt;/span&gt;Loading...&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;div&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;/span&gt;;
