&lt;blockquote&gt;
  &lt;p&gt;Preview multiple images before it is uploaded using jQuery/javascript?&lt;/p&gt;
&lt;/blockquote&gt;

&lt;p&gt;&lt;strong&gt;This will preview multiple files as thumbnail images at a time&lt;/strong&gt;&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;Html&lt;/strong&gt;&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&amp;lt;input id=&lt;span class=&quot;hljs-string&quot;&gt;&quot;ImageMedias&quot;&lt;/span&gt; multiple=&lt;span class=&quot;hljs-string&quot;&gt;&quot;multiple&quot;&lt;/span&gt; name=&lt;span class=&quot;hljs-string&quot;&gt;&quot;ImageMedias&quot;&lt;/span&gt; type=&lt;span class=&quot;hljs-string&quot;&gt;&quot;file&quot;&lt;/span&gt;
accept=&lt;span class=&quot;hljs-string&quot;&gt;&quot;.jfif,.jpg,.jpeg,.png,.gif&quot;&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&quot;custom-file-input&quot;&lt;/span&gt;  value=&lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&lt;/span&gt;&amp;gt;                                    
&lt;span class=&quot;language-xml&quot;&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;div&lt;/span&gt; &lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&quot;divImageMediaPreview&quot;&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;div&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;&lt;strong&gt;Script&lt;/strong&gt;&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;$(&lt;span class=&quot;hljs-string&quot;&gt;&quot;#ImageMedias&quot;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;change&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;typeof&lt;/span&gt; (&lt;span class=&quot;hljs-title class_&quot;&gt;FileReader&lt;/span&gt;) != &lt;span class=&quot;hljs-string&quot;&gt;&quot;undefined&quot;&lt;/span&gt;) {
        &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; dvPreview = $(&lt;span class=&quot;hljs-string&quot;&gt;&quot;#divImageMediaPreview&quot;&lt;/span&gt;);
        dvPreview.&lt;span class=&quot;hljs-title function_&quot;&gt;html&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&lt;/span&gt;);            
        $($(&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;)[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;].&lt;span class=&quot;hljs-property&quot;&gt;files&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;each&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
            &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; file = $(&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;);                
                &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; reader = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;FileReader&lt;/span&gt;();
                reader.&lt;span class=&quot;hljs-property&quot;&gt;onload&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;e&lt;/span&gt;) {
                    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; img = $(&lt;span class=&quot;hljs-string&quot;&gt;&quot;&amp;lt;img /&amp;gt;&quot;&lt;/span&gt;);
                    img.&lt;span class=&quot;hljs-title function_&quot;&gt;attr&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;style&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;width: 150px; height:100px; padding: 10px&quot;&lt;/span&gt;);
                    img.&lt;span class=&quot;hljs-title function_&quot;&gt;attr&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;src&quot;&lt;/span&gt;, e.&lt;span class=&quot;hljs-property&quot;&gt;target&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;result&lt;/span&gt;);
                    dvPreview.&lt;span class=&quot;hljs-title function_&quot;&gt;append&lt;/span&gt;(img);
                }
                reader.&lt;span class=&quot;hljs-title function_&quot;&gt;readAsDataURL&lt;/span&gt;(file[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;]);                
        });
    } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
        &lt;span class=&quot;hljs-title function_&quot;&gt;alert&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;This browser does not support HTML5 FileReader.&quot;&lt;/span&gt;);
    }
});
&lt;/code&gt;&lt;/pre&gt;

