                                                        &lt;span class=&quot;hljs-keyword&quot;&gt;SELECT&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@FinalCode&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@FinalCode&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt;    &lt;span class=&quot;hljs-string&quot;&gt;&apos;, [ID&apos;&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;CAST&lt;/span&gt;((&lt;span class=&quot;hljs-variable&quot;&gt;@y&lt;/span&gt;) &lt;span class=&quot;hljs-keyword&quot;&gt;as&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;varchar&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;)) &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;.&apos;&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;SELECT&lt;/span&gt; [ColumnName] &lt;span class=&quot;hljs-keyword&quot;&gt;FROM&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@VAR&lt;/span&gt;_TAB &lt;span class=&quot;hljs-keyword&quot;&gt;WHERE&lt;/span&gt; [VAR_ID] &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@z&lt;/span&gt;) &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;] =  ID&apos;&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;CAST&lt;/span&gt;((&lt;span class=&quot;hljs-variable&quot;&gt;@y&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;) &lt;span class=&quot;hljs-keyword&quot;&gt;as&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;varchar&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;)) &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;.&apos;&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;SELECT&lt;/span&gt; [ColumnName] &lt;span class=&quot;hljs-keyword&quot;&gt;FROM&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@VAR&lt;/span&gt;_TAB &lt;span class=&quot;hljs-keyword&quot;&gt;WHERE&lt;/span&gt; [VAR_ID] &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@z&lt;/span&gt;)
                                                        &lt;span class=&quot;hljs-keyword&quot;&gt;SELECT&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@z&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@z&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;
                                                    &lt;span class=&quot;hljs-keyword&quot;&gt;END&lt;/span&gt;
                                                    &lt;span class=&quot;hljs-keyword&quot;&gt;SELECT&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@y&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@y&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;
                                                &lt;span class=&quot;hljs-keyword&quot;&gt;END&lt;/span&gt;
        &lt;span class=&quot;hljs-keyword&quot;&gt;SELECT&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@FinalCode&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@FinalCode&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; 
                                        &lt;span class=&quot;hljs-string&quot;&gt;&apos; FROM ( SELECT * FROM ##ALL_StableColumns)ID1&apos;&lt;/span&gt;;
                                        &lt;span class=&quot;hljs-keyword&quot;&gt;SELECT&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@y&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;
                                        WHILE &lt;span class=&quot;hljs-variable&quot;&gt;@y&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;&amp;lt;=&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;SELECT&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;MAX&lt;/span&gt;([xxx_ID_xxx]) &lt;span class=&quot;hljs-keyword&quot;&gt;FROM&lt;/span&gt; ##FinalTab)
                                        &lt;span class=&quot;hljs-keyword&quot;&gt;BEGIN&lt;/span&gt;
                                            &lt;span class=&quot;hljs-keyword&quot;&gt;SELECT&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@x&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;
                                            &lt;span class=&quot;hljs-keyword&quot;&gt;SELECT&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@FinalCode&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@FinalCode&lt;/span&gt; 
                                                                                &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos; LEFT JOIN (SELECT &apos;&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt;  &lt;span class=&quot;hljs-variable&quot;&gt;@ListOfColumns&lt;/span&gt;_Stable &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos; , &apos;&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@ListOfColumns&lt;/span&gt;_Variable 
                                                                                &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos; FROM ##FinalTab WHERE [xxx_ID_xxx] = &apos;&lt;/span&gt; 
                                                                                &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;CAST&lt;/span&gt;(&lt;span class=&quot;hljs-variable&quot;&gt;@y&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;as&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;varchar&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;)) &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos; )ID&apos;&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;CAST&lt;/span&gt;((&lt;span class=&quot;hljs-variable&quot;&gt;@y&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;) &lt;span class=&quot;hljs-keyword&quot;&gt;as&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;varchar&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;))  
                                                                                &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos; ON 1 = 1&apos;&lt;/span&gt; 
                                                                                WHILE &lt;span class=&quot;hljs-variable&quot;&gt;@x&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;&amp;lt;=&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;SELECT&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;MAX&lt;/span&gt;([STAB_ID]) &lt;span class=&quot;hljs-keyword&quot;&gt;FROM&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@STAB&lt;/span&gt;_TAB)
                                                                                &lt;span class=&quot;hljs-keyword&quot;&gt;BEGIN&lt;/span&gt;
                                                                                    &lt;span class=&quot;hljs-keyword&quot;&gt;SELECT&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@FinalCode&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@FinalCode&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos; AND ID1.&apos;&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;SELECT&lt;/span&gt; [ColumnName] &lt;span class=&quot;hljs-keyword&quot;&gt;FROM&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@STAB&lt;/span&gt;_TAB &lt;span class=&quot;hljs-keyword&quot;&gt;WHERE&lt;/span&gt; [STAB_ID] &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@x&lt;/span&gt;) &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos; = ID&apos;&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;CAST&lt;/span&gt;((&lt;span class=&quot;hljs-variable&quot;&gt;@y&lt;/span&gt;&lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt;&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;) &lt;span class=&quot;hljs-keyword&quot;&gt;as&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;varchar&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;)) &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;&apos;.&apos;&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;SELECT&lt;/span&gt; [ColumnName] &lt;span class=&quot;hljs-keyword&quot;&gt;FROM&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@STAB&lt;/span&gt;_TAB &lt;span class=&quot;hljs-keyword&quot;&gt;WHERE&lt;/span&gt; [STAB_ID] &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@x&lt;/span&gt;)
                                                                                    &lt;span class=&quot;hljs-keyword&quot;&gt;SELECT&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@x&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@x&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt;&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;
                                                                                &lt;span class=&quot;hljs-keyword&quot;&gt;END&lt;/span&gt;
                                            &lt;span class=&quot;hljs-keyword&quot;&gt;SELECT&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@y&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;@y&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;+&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;
                                        &lt;span class=&quot;hljs-keyword&quot;&gt;END&lt;/span&gt;

&lt;span class=&quot;hljs-keyword&quot;&gt;SELECT&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;*&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;FROM&lt;/span&gt; ##ALL_Dimentions;
&lt;span class=&quot;hljs-keyword&quot;&gt;EXECUTE&lt;/span&gt; SP_EXECUTESQL &lt;span class=&quot;hljs-variable&quot;&gt;@FinalCode&lt;/span&gt;;
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;From executing the first query (by passing source DB and table name) you will get a pre-created execution query for the second SP, all you have to do is define is the column from your source:
+ Stable
+ Value (will be used to concentrate values based on that)
+ Dim (column you want to use to pivot by)&lt;/p&gt;

&lt;p&gt;Names and datatypes will be defined automatically!&lt;/p&gt;

&lt;p&gt;I cant recommend it for any production environments but does the job for adhoc BI requests. &lt;/p&gt;

&lt;p&gt;Please try&lt;/p&gt;
&lt;pre class=&quot;lang-sql s-code-block&quot;&gt;&lt;code class=&quot;hljs language-sql&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;CREATE&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;TABLE&lt;/span&gt; pvt (Present &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt;, [Absent] &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt;);
GO
&lt;span class=&quot;hljs-keyword&quot;&gt;INSERT&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;INTO&lt;/span&gt; pvt &lt;span class=&quot;hljs-keyword&quot;&gt;VALUES&lt;/span&gt; (&lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;40&lt;/span&gt;);
GO
&lt;span class=&quot;hljs-comment&quot;&gt;--Unpivot the table.&lt;/span&gt;
&lt;span class=&quot;hljs-keyword&quot;&gt;SELECT&lt;/span&gt; Code, &lt;span class=&quot;hljs-keyword&quot;&gt;Value&lt;/span&gt;
&lt;span class=&quot;hljs-keyword&quot;&gt;FROM&lt;/span&gt; 
   (&lt;span class=&quot;hljs-keyword&quot;&gt;SELECT&lt;/span&gt; Present, Absent
   &lt;span class=&quot;hljs-keyword&quot;&gt;FROM&lt;/span&gt; pvt) p
UNPIVOT
   (&lt;span class=&quot;hljs-keyword&quot;&gt;Value&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;FOR&lt;/span&gt; Code &lt;span class=&quot;hljs-keyword&quot;&gt;IN&lt;/span&gt; 
      (Present, [Absent])
)&lt;span class=&quot;hljs-keyword&quot;&gt;AS&lt;/span&gt; unpvt;
GO

&lt;span class=&quot;hljs-keyword&quot;&gt;DROP&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;TABLE&lt;/span&gt; pvt
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;One more option which can be very useful is suing CROSS APPLY&lt;/p&gt;
&lt;pre class=&quot;lang-sql s-code-block&quot;&gt;&lt;code class=&quot;hljs language-sql&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;-- Original data&lt;/span&gt;
&lt;span class=&quot;hljs-keyword&quot;&gt;SELECT&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;*&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;FROM&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;VALUES&lt;/span&gt; (&lt;span class=&quot;hljs-string&quot;&gt;&apos;1&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;),(&lt;span class=&quot;hljs-string&quot;&gt;&apos;2&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;11&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;22&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;33&lt;/span&gt;)) &lt;span class=&quot;hljs-keyword&quot;&gt;AS&lt;/span&gt; Stage(id,col1,col2,col3)

&lt;span class=&quot;hljs-comment&quot;&gt;-- row to columns using CROSS APPLY&lt;/span&gt;
&lt;span class=&quot;hljs-keyword&quot;&gt;SELECT&lt;/span&gt; Stage.id,v.idd, v.colc
&lt;span class=&quot;hljs-keyword&quot;&gt;FROM&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;VALUES&lt;/span&gt; (&lt;span class=&quot;hljs-string&quot;&gt;&apos;1&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;),(&lt;span class=&quot;hljs-string&quot;&gt;&apos;2&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;11&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;22&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;33&lt;/span&gt;)) &lt;span class=&quot;hljs-keyword&quot;&gt;AS&lt;/span&gt; Stage(id,col1,col2,col3)
&lt;span class=&quot;hljs-keyword&quot;&gt;CROSS&lt;/span&gt; APPLY (&lt;span class=&quot;hljs-keyword&quot;&gt;VALUES&lt;/span&gt; (&lt;span class=&quot;hljs-string&quot;&gt;&apos;col1&apos;&lt;/span&gt;, col1),(&lt;span class=&quot;hljs-string&quot;&gt;&apos;col2&apos;&lt;/span&gt;, col2),(&lt;span class=&quot;hljs-string&quot;&gt;&apos;col3&apos;&lt;/span&gt;, col3)) &lt;span class=&quot;hljs-keyword&quot;&gt;AS&lt;/span&gt; v(idd,colc)
GO
&lt;/code&gt;&lt;/pre&gt;

419:
&lt;p&gt;&lt;strong&gt;(StartA &amp;lt;= EndB)  and  (EndA &amp;gt;= StartB)&lt;/strong&gt;&lt;/p&gt;
&lt;p&gt;&lt;em&gt;Proof:&lt;/em&gt;&lt;br&gt;
Let ConditionA Mean that DateRange A Completely After DateRange B&lt;/p&gt;
&lt;pre&gt;&lt;code&gt;_                        |---- DateRange A ------|
|---Date Range B -----|                          _
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;(True if &lt;code&gt;StartA &amp;gt; EndB&lt;/code&gt;)&lt;/p&gt;
&lt;p&gt;Let ConditionB Mean that DateRange A is Completely Before DateRange B&lt;/p&gt;
&lt;pre&gt;&lt;code&gt;|---- DateRange A -----|                        _ 
_                          |---Date Range B ----|
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;(True if &lt;code&gt;EndA &amp;lt; StartB&lt;/code&gt;)&lt;/p&gt;
&lt;p&gt;Then Overlap exists if Neither A Nor B is true -&lt;br&gt;
(If one range is neither completely after the other,&lt;br&gt;
nor completely before the other,
then they must overlap.)&lt;/p&gt;
&lt;p&gt;Now one of &lt;a href=&quot;https://en.wikipedia.org/wiki/De_Morgan%27s_laws&quot; rel=&quot;noreferrer&quot;&gt;De Morgan&apos;s laws&lt;/a&gt; says that:&lt;/p&gt;
&lt;p&gt;&lt;code&gt;Not (A Or B)&lt;/code&gt;  &amp;lt;=&amp;gt;  &lt;code&gt;Not A And Not B&lt;/code&gt;&lt;/p&gt;
&lt;p&gt;Which translates to: &lt;code&gt;(StartA &amp;lt;= EndB)  and  (EndA &amp;gt;= StartB)&lt;/code&gt;&lt;/p&gt;
&lt;hr&gt;
&lt;p&gt;NOTE: This includes conditions where the edges overlap exactly.  If you wish to exclude that,&lt;br&gt;
change the &lt;code&gt;&amp;gt;=&lt;/code&gt; operators to &lt;code&gt;&amp;gt;&lt;/code&gt;, and &lt;code&gt;&amp;lt;=&lt;/code&gt;  to &lt;code&gt;&amp;lt;&lt;/code&gt;&lt;/p&gt;
&lt;hr&gt;
&lt;p&gt;NOTE2. Thanks to @Baodad, see &lt;a href=&quot;http://baodad.blogspot.com/2014/06/date-range-overlap.html&quot; rel=&quot;noreferrer&quot;&gt;this blog&lt;/a&gt;, the actual overlap is least of:&lt;br&gt;
{ &lt;code&gt;endA-startA&lt;/code&gt;, &lt;code&gt;endA - startB&lt;/code&gt;, &lt;code&gt;endB-startA&lt;/code&gt;, &lt;code&gt;endB - startB&lt;/code&gt; }&lt;/p&gt;
&lt;p&gt;&lt;code&gt;(StartA &amp;lt;= EndB)  and  (EndA &amp;gt;= StartB)&lt;/code&gt;
&lt;code&gt;(StartA &amp;lt;= EndB)  and  (StartB &amp;lt;= EndA)&lt;/code&gt;&lt;/p&gt;
&lt;hr&gt;
&lt;p&gt;NOTE3. Thanks to @tomosius, a shorter version reads:&lt;br&gt;
&lt;code&gt;DateRangesOverlap = max(start1, start2) &amp;lt; min(end1, end2)&lt;/code&gt;&lt;br&gt;
This is actually a syntactical shortcut for what is a longer implementation, which includes extra checks to verify that the start dates are on or before the endDates.  Deriving this from above:&lt;/p&gt;
&lt;p&gt;If start and end dates can be out of order, i.e., if it is possible that &lt;code&gt;startA &amp;gt; endA&lt;/code&gt; or &lt;code&gt;startB &amp;gt; endB&lt;/code&gt;, then you also have to check that they are in order, so that means you have to add two additional validity rules:&lt;br&gt;
&lt;code&gt;(StartA &amp;lt;= EndB) and (StartB &amp;lt;= EndA) and (StartA &amp;lt;= EndA) and (StartB &amp;lt;= EndB)&lt;/code&gt;
or:&lt;br&gt;
&lt;code&gt;(StartA &amp;lt;= EndB) and (StartA &amp;lt;= EndA) and (StartB &amp;lt;= EndA) and (StartB &amp;lt;= EndB)&lt;/code&gt;
or,&lt;br&gt;
&lt;code&gt;(StartA &amp;lt;= Min(EndA, EndB) and (StartB &amp;lt;= Min(EndA, EndB))&lt;/code&gt;
or:&lt;br&gt;
&lt;code&gt;(Max(StartA, StartB) &amp;lt;= Min(EndA, EndB)&lt;/code&gt;&lt;/p&gt;
&lt;p&gt;But to implement &lt;code&gt;Min()&lt;/code&gt; and &lt;code&gt;Max()&lt;/code&gt;, you have to code, (using C ternary for terseness),:&lt;br&gt;
&lt;code&gt;(StartA &amp;gt; StartB? Start A: StartB) &amp;lt;= (EndA &amp;lt; EndB? EndA: EndB)&lt;/code&gt;&lt;/p&gt;
&lt;p&gt;NOTE4.  Thanks to Carl for noticing this, but  &lt;a href=&quot;https://stackoverflow.com/questions/27430253/find-if-given-date-range-inside-another-date-range-in-mysql/27430668#27430668&quot;&gt;another answer&lt;/a&gt; shows an equivalent mathematical expression for this logical expression. Because the product of any two real numbers with opposite sign is negative and with the same sign it is positive, if you convert the datetimes to fractional numbers (and most DBMSs internally use numbers to represent datetimes), the above logical expression can also be evaluated using the following mathematical expression:&lt;/p&gt;
&lt;pre&gt;&lt;code&gt;(EndA  - StartA) * (StartB - EndB) &amp;lt;= 0
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;I believe that it is sufficient to say that the two ranges overlap if:&lt;/p&gt;

&lt;pre&gt;&lt;code&gt;(StartDate1 &amp;lt;= EndDate2) and (StartDate2 &amp;lt;= EndDate1)
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;For reasoning about temporal relations (or any other interval relations, come to that), consider &lt;a href=&quot;http://en.wikipedia.org/wiki/Allen%27s_Interval_Algebra&quot; rel=&quot;noreferrer&quot;&gt;Allen&apos;s Interval Algebra&lt;/a&gt;.  It describes the 13 possible relations that two intervals can have with respect to each other.  You can find other references  &quot;Allen Interval&quot; seems to be an operative search term.  You can also find information about these operations in Snodgrass&apos;s &lt;a href=&quot;http://www.cs.arizona.edu/~rts/publications.html&quot; rel=&quot;noreferrer&quot;&gt;Developing Time-Oriented Applications in SQL&lt;/a&gt; (PDF available online at URL), and in Date, Darwen and Lorentzos &lt;a href=&quot;https://rads.stackoverflow.com/amzn/click/com/B005UY0W0E&quot; rel=&quot;noreferrer&quot;&gt;Temporal Data and the Relational Model&lt;/a&gt; (2002) or  &lt;a href=&quot;https://rads.stackoverflow.com/amzn/click/com/B00N2SNSUG&quot; rel=&quot;noreferrer&quot;&gt;Time and Relational Theory: Temporal Databases in the Relational Model and SQL&lt;/a&gt; (2014; effectively the second edition of TD&amp;amp;RM).&lt;/p&gt;

&lt;hr&gt;

&lt;p&gt;The short(ish) answer is: given two date intervals &lt;code&gt;A&lt;/code&gt; and &lt;code&gt;B&lt;/code&gt; with components &lt;code&gt;.start&lt;/code&gt; and &lt;code&gt;.end&lt;/code&gt; and the constraint &lt;code&gt;.start &amp;lt;= .end&lt;/code&gt;, then two intervals overlap if:&lt;/p&gt;

&lt;pre&gt;&lt;code&gt;A.end &amp;gt;= B.start AND A.start &amp;lt;= B.end
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;You can tune the use of &lt;code&gt;&amp;gt;=&lt;/code&gt; vs &lt;code&gt;&amp;gt;&lt;/code&gt; and &lt;code&gt;&amp;lt;=&lt;/code&gt; vs &lt;code&gt;&amp;lt;&lt;/code&gt; to meet your requirements for degree of overlap.&lt;/p&gt;

&lt;hr&gt;

&lt;p&gt;ErikE comments:&lt;/p&gt;

&lt;blockquote&gt;
  &lt;p&gt;You can only get 13 if you count things funny... I can get &quot;15 possible relations that two intervals can have&quot; when I go crazy with it. By sensible counting, I get only six, and if you throw out caring whether A or B comes first, I get only three (no intersect, partially intersect, one wholly within other). 15 goes like this: [before:before, start, within, end, after], [start:start, within, end, after], [within:within, end, after], [end:end, after], [after:after]. &lt;/p&gt;
&lt;/blockquote&gt;

&lt;p&gt;I think that you cannot count the two entries &apos;before:before&apos; and &apos;after:after&apos;.  I could see 7 entries if you equate some relations with their inverses (see the diagram in the referenced Wikipedia URL; it has 7 entries, 6 of which have a different inverse, with equals not having a distinct inverse).  And whether three is sensible depends on your requirements.&lt;/p&gt;

&lt;pre&gt;&lt;code&gt;----------------------|-------A-------|----------------------
    |----B1----|
           |----B2----|
               |----B3----|
               |----------B4----------|
               |----------------B5----------------|
                      |----B6----|
----------------------|-------A-------|----------------------
                      |------B7-------|
                      |----------B8-----------|
                         |----B9----|
                         |----B10-----|
                         |--------B11--------|
                                      |----B12----|
                                         |----B13----|
----------------------|-------A-------|----------------------
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;If the overlap itself should be calculated as well, you can use the following formula:&lt;/p&gt;

&lt;pre&gt;&lt;code&gt;overlap = max(0, min(EndDate1, EndDate2) - max(StartDate1, StartDate2))
if (overlap &amp;gt; 0) { 
    ...
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;All the solutions that check a multitude of conditions based on where the ranges are in relation to one another can be greatly simplified by &lt;em&gt;&lt;strong&gt;simply ensuring that one range starts before or at the same time as the other.&lt;/strong&gt;&lt;/em&gt; You can do this by swapping the ranges if necessary up front.&lt;/p&gt;
&lt;p&gt;Then, you can detect overlap if the second range start is either:&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;less than or equal to the first range end (if ranges are inclusive, containing both the start and end times); or&lt;/li&gt;
&lt;li&gt;less than (if ranges are inclusive of start and exclusive of end).&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;For example (assuming inclusive at both ends), there&apos;s only four possibilities for range 2of which &lt;em&gt;one&lt;/em&gt; is a non-overlap (the &lt;code&gt;&amp;gt;&lt;/code&gt; at the end of the range means it doesn&apos;t matter where the range ends):&lt;/p&gt;
&lt;pre class=&quot;lang-none s-code-block&quot;&gt;&lt;code&gt;|-----|        range 1, lines below are all range 2.
|--&amp;gt;  :        overlap.
 |--&amp;gt; :        overlap.
      |---&amp;gt;    overlap (no overlap in exclusive-of-end case).
       |---&amp;gt;   no overlap.
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;The &lt;em&gt;endpoint&lt;/em&gt; of the second range doesn&apos;t affect the result at all. So, in pseudo-code, you can do something like (assuming &lt;code&gt;s &amp;lt;= e&lt;/code&gt; within a given range):&lt;/p&gt;
&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;def&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;hasOverlap&lt;/span&gt; (r1, r2):
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; r1.s &amp;gt; r2.s: swap r1, r2
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; r2.s &amp;lt;= r1.e
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;If the ranges are &lt;em&gt;exclusive&lt;/em&gt; at the end, you just have to replace &lt;code&gt;&amp;lt;=&lt;/code&gt; with &lt;code&gt;&amp;lt;&lt;/code&gt; in the expression you return.&lt;/p&gt;
&lt;p&gt;This greatly limits the number of checks you have to make because you remove half of the problem space early by ensuring the first range never starts after the second.&lt;/p&gt;
&lt;hr&gt;
&lt;p&gt;And, since &quot;code talks&quot;, here is some Python code that shows this in action, with quite a few test cases. First, the &lt;code&gt;InclusiveRange&lt;/code&gt; class:&lt;/p&gt;
&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;InclusiveRange&lt;/span&gt;:
    &lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&quot;InclusiveRange class to represent a lower and upper bound&quot;&quot;&quot;&lt;/span&gt;

    &lt;span class=&quot;hljs-keyword&quot;&gt;def&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;__init__&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;self, start, end&lt;/span&gt;):
        &lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&quot;Initialisation ensures start &amp;lt;= end&quot;&quot;&quot;&lt;/span&gt;
        self.start = &lt;span class=&quot;hljs-built_in&quot;&gt;min&lt;/span&gt;(start, end)
        self.end = &lt;span class=&quot;hljs-built_in&quot;&gt;max&lt;/span&gt;(start, end)

    &lt;span class=&quot;hljs-keyword&quot;&gt;def&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;__repr__&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;self&lt;/span&gt;):
        &lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&quot; Return representation for f-string&quot;&quot;&quot;&lt;/span&gt;
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-string&quot;&gt;f&quot;(&lt;span class=&quot;hljs-subst&quot;&gt;{self.start}&lt;/span&gt;, &lt;span class=&quot;hljs-subst&quot;&gt;{self.end}&lt;/span&gt;)&quot;&lt;/span&gt;

    &lt;span class=&quot;hljs-keyword&quot;&gt;def&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;overlaps&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;self, other&lt;/span&gt;):
        &lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&quot;True if range overlaps with another&quot;&quot;&quot;&lt;/span&gt;

        &lt;span class=&quot;hljs-comment&quot;&gt;# Very limited recursion to ensure start of first range&lt;/span&gt;
        &lt;span class=&quot;hljs-comment&quot;&gt;# isn&apos;t after start of second.&lt;/span&gt;

        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; self.start &amp;gt; other.start:
            &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; other.overlaps(self)

        &lt;span class=&quot;hljs-comment&quot;&gt;# Greatly simplified check for overlap.&lt;/span&gt;

        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; other.start &amp;lt;= self.end
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Then a test case handler to allow us to nicely present the result of a &lt;em&gt;single&lt;/em&gt; test case:&lt;/p&gt;
&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;def&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;test_case&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;range1, range2&lt;/span&gt;) -&amp;gt; &lt;span class=&quot;hljs-literal&quot;&gt;None&lt;/span&gt;:
    &lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&quot;Single test case checker&quot;&quot;&quot;&lt;/span&gt;

    &lt;span class=&quot;hljs-comment&quot;&gt;# Get low and high value for &quot;graphic&quot; output.&lt;/span&gt;

    low = &lt;span class=&quot;hljs-built_in&quot;&gt;min&lt;/span&gt;(range1.start, range2.start)
    high = &lt;span class=&quot;hljs-built_in&quot;&gt;max&lt;/span&gt;(range1.end, range2.end)

    &lt;span class=&quot;hljs-comment&quot;&gt;# Output ranges and graphic.&lt;/span&gt;

    &lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;f&quot;r1=&lt;span class=&quot;hljs-subst&quot;&gt;{range1}&lt;/span&gt; r2=&lt;span class=&quot;hljs-subst&quot;&gt;{range2}&lt;/span&gt;: &quot;&lt;/span&gt;, end=&lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&lt;/span&gt;)
    &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; val &lt;span class=&quot;hljs-keyword&quot;&gt;in&lt;/span&gt; &lt;span class=&quot;hljs-built_in&quot;&gt;range&lt;/span&gt;(low, high + &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;):
        is_in_first = range1.start &amp;lt;= val &amp;lt;= range1.end
        is_in_second = range2.start &amp;lt;= val &amp;lt;= range2.end

        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; is_in_first &lt;span class=&quot;hljs-keyword&quot;&gt;and&lt;/span&gt; is_in_second:
            &lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;|&quot;&lt;/span&gt;, end=&lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&lt;/span&gt;)
        &lt;span class=&quot;hljs-keyword&quot;&gt;elif&lt;/span&gt; is_in_first:
            &lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;&apos;&quot;&lt;/span&gt;, end=&lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&lt;/span&gt;)
        &lt;span class=&quot;hljs-keyword&quot;&gt;elif&lt;/span&gt; is_in_second:
            &lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;,&quot;&lt;/span&gt;, end=&lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&lt;/span&gt;)
        &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt;:
            &lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot; &quot;&lt;/span&gt;, end=&lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&lt;/span&gt;)

    &lt;span class=&quot;hljs-comment&quot;&gt;# Finally, output result of overlap check.&lt;/span&gt;

    &lt;span class=&quot;hljs-built_in&quot;&gt;print&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;f&quot; - &lt;span class=&quot;hljs-subst&quot;&gt;{range1.overlaps(range2)}&lt;/span&gt;\n&quot;&lt;/span&gt;)
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Then finally, a decent chunk of test cases to which you can add your own if need be:&lt;/p&gt;
&lt;pre class=&quot;lang-py s-code-block&quot;&gt;&lt;code class=&quot;hljs language-python&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;# Various test cases, add others if you doubt the correctness.&lt;/span&gt;

test_case(InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;), InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;8&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;))
test_case(InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;), InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;))
test_case(InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;), InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;))
test_case(InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;), InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;))
test_case(InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;), InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;))
test_case(InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;), InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;))
test_case(InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;), InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;))

test_case(InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;8&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;), InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;))
test_case(InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;), InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;))
test_case(InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;), InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;))
test_case(InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;), InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;7&lt;/span&gt;))
test_case(InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;), InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;))
test_case(InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;), InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;))
test_case(InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt;), InclusiveRange(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;9&lt;/span&gt;))
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Running that produces the output:&lt;/p&gt;
&lt;pre class=&quot;lang-none s-code-block&quot;&gt;&lt;code&gt;r1=(0, 1) r2=(8, 9): &apos;&apos;      ,, - False
r1=(0, 4) r2=(5, 9): &apos;&apos;&apos;&apos;&apos;,,,,, - False
r1=(0, 4) r2=(4, 9): &apos;&apos;&apos;&apos;|,,,,, - True
r1=(0, 7) r2=(2, 9): &apos;&apos;||||||,, - True
r1=(0, 4) r2=(0, 9): |||||,,,,, - True
r1=(0, 9) r2=(0, 9): |||||||||| - True
r1=(0, 9) r2=(4, 5): &apos;&apos;&apos;&apos;||&apos;&apos;&apos;&apos; - True
r1=(8, 9) r2=(0, 1): ,,      &apos;&apos; - False
r1=(5, 9) r2=(0, 4): ,,,,,&apos;&apos;&apos;&apos;&apos; - False
r1=(4, 9) r2=(0, 4): ,,,,|&apos;&apos;&apos;&apos;&apos; - True
r1=(2, 9) r2=(0, 7): ,,||||||&apos;&apos; - True
r1=(0, 9) r2=(0, 4): |||||&apos;&apos;&apos;&apos;&apos; - True
r1=(0, 9) r2=(0, 9): |||||||||| - True
r1=(4, 5) r2=(0, 9): ,,,,||,,,, - True
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;where each line has:&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;the two ranges being evaluated;&lt;/li&gt;
&lt;li&gt;a graphical representation of the &quot;range space&quot; (from lowest start to highest end) where each character is a value in that &quot;range space&quot;: &lt;code&gt;&apos;&lt;/code&gt; indicates a value in the first range only, &lt;code&gt;,&lt;/code&gt; indicates a value in the second range only, &lt;code&gt;|&lt;/code&gt; indicates a value in both ranges, and &lt;code&gt; &lt;/code&gt; indicates a value in neither range.&lt;/li&gt;
&lt;li&gt;the result of the overlap check.&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;You can see quite clearly that you only get true in the overlap check when there is at least one value in &lt;em&gt;both&lt;/em&gt; ranges (i.e., a &lt;code&gt;|&lt;/code&gt; character). Every other case gives false.&lt;/p&gt;
&lt;p&gt;Feel free to use any other values if you want to add more test cases.&lt;/p&gt;

&lt;p&gt;Here is yet another solution using JavaScript. Specialities of my solution:&lt;/p&gt;

&lt;ul&gt;
&lt;li&gt;Handles null values as infinity&lt;/li&gt;
&lt;li&gt;Assumes that the lower bound is inclusive and the upper bound exclusive.&lt;/li&gt;
&lt;li&gt;Comes with a bunch of tests&lt;/li&gt;
&lt;/ul&gt;

&lt;p&gt;The tests are based on integers but since date objects in JavaScript are comparable you can just throw in two date objects as well. Or you could throw in the millisecond timestamp.&lt;/p&gt;

&lt;h1&gt;Code:&lt;/h1&gt;

&lt;pre class=&quot;lang-javascript s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;/**
 * Compares to comparable objects to find out whether they overlap.
 * It is assumed that the interval is in the format [from,to) (read: from is inclusive, to is exclusive).
 * A null value is interpreted as infinity
 */&lt;/span&gt;
&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;intervalsOverlap&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;from1, to1, from2, to2&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; (to2 === &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt; || from1 &amp;lt; to2) &amp;amp;&amp;amp; (to1 === &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt; || to1 &amp;gt; from2);
}
&lt;/code&gt;&lt;/pre&gt;

&lt;h1&gt;Tests:&lt;/h1&gt;

&lt;pre class=&quot;lang-javascript s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-title function_&quot;&gt;describe&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;generateTest&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;firstRange, secondRange, expected&lt;/span&gt;) {
        &lt;span class=&quot;hljs-title function_&quot;&gt;it&lt;/span&gt;(&lt;span class=&quot;hljs-title class_&quot;&gt;JSON&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;stringify&lt;/span&gt;(firstRange) + &lt;span class=&quot;hljs-string&quot;&gt;&apos; and &apos;&lt;/span&gt; + &lt;span class=&quot;hljs-title class_&quot;&gt;JSON&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;stringify&lt;/span&gt;(secondRange), &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
            &lt;span class=&quot;hljs-title function_&quot;&gt;expect&lt;/span&gt;(&lt;span class=&quot;hljs-title function_&quot;&gt;intervalsOverlap&lt;/span&gt;(firstRange[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;], firstRange[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;], secondRange[&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;], secondRange[&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;])).&lt;span class=&quot;hljs-title function_&quot;&gt;toBe&lt;/span&gt;(expected);
        });
    }

    &lt;span class=&quot;hljs-title function_&quot;&gt;describe&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;no overlap (touching ends)&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
        &lt;span class=&quot;hljs-title function_&quot;&gt;generateTest&lt;/span&gt;([&lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;20&lt;/span&gt;], [&lt;span class=&quot;hljs-number&quot;&gt;20&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;30&lt;/span&gt;], &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;);
        &lt;span class=&quot;hljs-title function_&quot;&gt;generateTest&lt;/span&gt;([&lt;span class=&quot;hljs-number&quot;&gt;20&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;30&lt;/span&gt;], [&lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;20&lt;/span&gt;], &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;);

        &lt;span class=&quot;hljs-title function_&quot;&gt;generateTest&lt;/span&gt;([&lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;20&lt;/span&gt;], [&lt;span class=&quot;hljs-number&quot;&gt;20&lt;/span&gt;,&lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;], &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;);
        &lt;span class=&quot;hljs-title function_&quot;&gt;generateTest&lt;/span&gt;([&lt;span class=&quot;hljs-number&quot;&gt;20&lt;/span&gt;,&lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;], [&lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;20&lt;/span&gt;], &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;);

        &lt;span class=&quot;hljs-title function_&quot;&gt;generateTest&lt;/span&gt;([&lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;20&lt;/span&gt;], [&lt;span class=&quot;hljs-number&quot;&gt;20&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;30&lt;/span&gt;], &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;);
