    });

    &lt;span class=&quot;hljs-title function_&quot;&gt;describe&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;do overlap (one end overlaps)&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
