        &lt;span class=&quot;hljs-title function_&quot;&gt;generateTest&lt;/span&gt;([&lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;30&lt;/span&gt;], [&lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;20&lt;/span&gt;], &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);
        &lt;span class=&quot;hljs-title function_&quot;&gt;generateTest&lt;/span&gt;([&lt;span class=&quot;hljs-number&quot;&gt;19&lt;/span&gt;,&lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;], [&lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;20&lt;/span&gt;], &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);
    });

    &lt;span class=&quot;hljs-title function_&quot;&gt;describe&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;do overlap (one range included in other range)&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
        &lt;span class=&quot;hljs-title function_&quot;&gt;generateTest&lt;/span&gt;([&lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;40&lt;/span&gt;], [&lt;span class=&quot;hljs-number&quot;&gt;20&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;30&lt;/span&gt;], &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);
        &lt;span class=&quot;hljs-title function_&quot;&gt;generateTest&lt;/span&gt;([&lt;span class=&quot;hljs-number&quot;&gt;20&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;30&lt;/span&gt;], [&lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;40&lt;/span&gt;], &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);

        &lt;span class=&quot;hljs-title function_&quot;&gt;generateTest&lt;/span&gt;([&lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;,&lt;span class=&quot;hljs-number&quot;&gt;40&lt;/span&gt;], [&lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;,&lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;], &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);
