&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;    B  &lt;span class=&quot;hljs-number&quot;&gt;4.2242&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;219.092499&lt;/span&gt;    &lt;span class=&quot;hljs-number&quot;&gt;4.6831656&lt;/span&gt;
&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt;    C &lt;span class=&quot;hljs-operator&quot;&gt;-&lt;/span&gt;&lt;span class=&quot;hljs-number&quot;&gt;0.3181&lt;/span&gt;  &lt;span class=&quot;hljs-number&quot;&gt;18.849686&lt;/span&gt;    &lt;span class=&quot;hljs-number&quot;&gt;0.4029175&lt;/span&gt;
