        props.put(&lt;span class=&quot;hljs-string&quot;&gt;&quot;mail.smtp.socketFactory.fallback&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;false&quot;&lt;/span&gt;);   
