        &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;setType&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(String type)&lt;/span&gt; {   
