&lt;div class=&quot;snippet-code&quot;&gt;
&lt;pre class=&quot;snippet-code-js lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; array = [[&lt;span class=&quot;hljs-string&quot;&gt;&quot;$6&quot;&lt;/span&gt;], [&lt;span class=&quot;hljs-string&quot;&gt;&quot;$12&quot;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&quot;$16&quot;&lt;/span&gt;], [&lt;span class=&quot;hljs-string&quot;&gt;&quot;$25&quot;&lt;/span&gt;], [&lt;span class=&quot;hljs-string&quot;&gt;&quot;$25&quot;&lt;/span&gt;], [&lt;span class=&quot;hljs-string&quot;&gt;&quot;$18&quot;&lt;/span&gt;], [&lt;span class=&quot;hljs-string&quot;&gt;&quot;$22&quot;&lt;/span&gt;], [&lt;span class=&quot;hljs-string&quot;&gt;&quot;$10&quot;&lt;/span&gt;]]
&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; a = array.&lt;span class=&quot;hljs-title function_&quot;&gt;flat&lt;/span&gt;(&lt;span class=&quot;hljs-title class_&quot;&gt;Infinity&lt;/span&gt;);
&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(a);&lt;/code&gt;&lt;/pre&gt;
