    &lt;span class=&quot;hljs-comment&quot;&gt;// Using Regular expression&lt;/span&gt;
    &lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;getElementById&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;result1&apos;&lt;/span&gt;).&lt;span class=&quot;hljs-property&quot;&gt;value&lt;/span&gt; = &lt;span class=&quot;hljs-title function_&quot;&gt;toCommas&lt;/span&gt;(&lt;span class=&quot;hljs-built_in&quot;&gt;parseInt&lt;/span&gt;(num1));
    &lt;span class=&quot;hljs-comment&quot;&gt;// Using toLocaleString()&lt;/span&gt;

    &lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;getElementById&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;result2&apos;&lt;/span&gt;).&lt;span class=&quot;hljs-property&quot;&gt;value&lt;/span&gt; = &lt;span class=&quot;hljs-built_in&quot;&gt;parseInt&lt;/span&gt;(num1).&lt;span class=&quot;hljs-title function_&quot;&gt;toLocaleString&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;ja-JP&apos;&lt;/span&gt;, {
      &lt;span class=&quot;hljs-attr&quot;&gt;style&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;currency&apos;&lt;/span&gt;,
      &lt;span class=&quot;hljs-attr&quot;&gt;currency&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;JPY&apos;&lt;/span&gt;
