&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-title function_&quot;&gt;formatNums&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;0000.0000&apos;&lt;/span&gt;));
