&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-title function_&quot;&gt;formatNums&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;100.2301&lt;/span&gt;));
