&lt;p&gt;There is a standards based replacement,&lt;code&gt;DOMContentLoaded&lt;/code&gt; that is supported by over &lt;a href=&quot;http://caniuse.com/#search=DOMContentLoaded&quot; rel=&quot;noreferrer&quot;&gt;99% of browsers&lt;/a&gt;, though not IE8:&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;addEventListener&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;DOMContentLoaded&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;event&lt;/span&gt;) { 
  &lt;span class=&quot;hljs-comment&quot;&gt;//do work&lt;/span&gt;
});
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;jQuery&apos;s native function is much more complicated than just window.onload, as depicted below.&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;bindReady&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; ( readyBound ) &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt;;
    readyBound = &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;;

    &lt;span class=&quot;hljs-comment&quot;&gt;// Mozilla, Opera and webkit nightlies currently support this event&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; ( &lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;addEventListener&lt;/span&gt; ) {
        &lt;span class=&quot;hljs-comment&quot;&gt;// Use the handy event callback&lt;/span&gt;
        &lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;addEventListener&lt;/span&gt;( &lt;span class=&quot;hljs-string&quot;&gt;&quot;DOMContentLoaded&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){
            &lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;removeEventListener&lt;/span&gt;( &lt;span class=&quot;hljs-string&quot;&gt;&quot;DOMContentLoaded&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-variable language_&quot;&gt;arguments&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;callee&lt;/span&gt;, &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt; );
            jQuery.&lt;span class=&quot;hljs-title function_&quot;&gt;ready&lt;/span&gt;();
        }, &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt; );

    &lt;span class=&quot;hljs-comment&quot;&gt;// If IE event model is used&lt;/span&gt;
    } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; ( &lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;attachEvent&lt;/span&gt; ) {
        &lt;span class=&quot;hljs-comment&quot;&gt;// ensure firing before onload,&lt;/span&gt;
        &lt;span class=&quot;hljs-comment&quot;&gt;// maybe late but safe also for iframes&lt;/span&gt;
        &lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;attachEvent&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;onreadystatechange&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; ( &lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;readyState&lt;/span&gt; === &lt;span class=&quot;hljs-string&quot;&gt;&quot;complete&quot;&lt;/span&gt; ) {
                &lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;detachEvent&lt;/span&gt;( &lt;span class=&quot;hljs-string&quot;&gt;&quot;onreadystatechange&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-variable language_&quot;&gt;arguments&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;callee&lt;/span&gt; );
                jQuery.&lt;span class=&quot;hljs-title function_&quot;&gt;ready&lt;/span&gt;();
            }
        });

        &lt;span class=&quot;hljs-comment&quot;&gt;// If IE and not an iframe&lt;/span&gt;
        &lt;span class=&quot;hljs-comment&quot;&gt;// continually check to see if the document is ready&lt;/span&gt;
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; ( &lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;documentElement&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;doScroll&lt;/span&gt; &amp;amp;&amp;amp; &lt;span class=&quot;hljs-variable language_&quot;&gt;window&lt;/span&gt; == &lt;span class=&quot;hljs-variable language_&quot;&gt;window&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;top&lt;/span&gt; ) (&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){
            &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; ( jQuery.&lt;span class=&quot;hljs-property&quot;&gt;isReady&lt;/span&gt; ) &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt;;

            &lt;span class=&quot;hljs-keyword&quot;&gt;try&lt;/span&gt; {
                &lt;span class=&quot;hljs-comment&quot;&gt;// If IE is used, use the trick by Diego Perini&lt;/span&gt;
                &lt;span class=&quot;hljs-comment&quot;&gt;// http://javascript.nwbox.com/IEContentLoaded/&lt;/span&gt;
                &lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;documentElement&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;doScroll&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;left&quot;&lt;/span&gt;);
            } &lt;span class=&quot;hljs-keyword&quot;&gt;catch&lt;/span&gt;( error ) {
                &lt;span class=&quot;hljs-built_in&quot;&gt;setTimeout&lt;/span&gt;( &lt;span class=&quot;hljs-variable language_&quot;&gt;arguments&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;callee&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt; );
                &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt;;
            }

            &lt;span class=&quot;hljs-comment&quot;&gt;// and execute any waiting functions&lt;/span&gt;
            jQuery.&lt;span class=&quot;hljs-title function_&quot;&gt;ready&lt;/span&gt;();
        })();
    }

    &lt;span class=&quot;hljs-comment&quot;&gt;// A fallback to window.onload, that will always work&lt;/span&gt;
    jQuery.&lt;span class=&quot;hljs-property&quot;&gt;event&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;add&lt;/span&gt;( &lt;span class=&quot;hljs-variable language_&quot;&gt;window&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;load&quot;&lt;/span&gt;, jQuery.&lt;span class=&quot;hljs-property&quot;&gt;ready&lt;/span&gt; );
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Edit:&lt;/p&gt;

&lt;h2&gt;Here is a viable replacement for jQuery ready&lt;/h2&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;ready&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;callback&lt;/span&gt;){
    &lt;span class=&quot;hljs-comment&quot;&gt;// in case the document is already rendered&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;readyState&lt;/span&gt;!=&lt;span class=&quot;hljs-string&quot;&gt;&apos;loading&apos;&lt;/span&gt;) &lt;span class=&quot;hljs-title function_&quot;&gt;callback&lt;/span&gt;();
    &lt;span class=&quot;hljs-comment&quot;&gt;// modern browsers&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;addEventListener&lt;/span&gt;) &lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;addEventListener&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;DOMContentLoaded&apos;&lt;/span&gt;, callback);
    &lt;span class=&quot;hljs-comment&quot;&gt;// IE &amp;lt;= 8&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;attachEvent&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;onreadystatechange&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;readyState&lt;/span&gt;==&lt;span class=&quot;hljs-string&quot;&gt;&apos;complete&apos;&lt;/span&gt;) &lt;span class=&quot;hljs-title function_&quot;&gt;callback&lt;/span&gt;();
    });
}

&lt;span class=&quot;hljs-title function_&quot;&gt;ready&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){
    &lt;span class=&quot;hljs-comment&quot;&gt;// do something&lt;/span&gt;
});
