  &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; _cryptoGuid = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
