                                       String permissions[], &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt;[] grantResults)&lt;/span&gt; {
    &lt;span class=&quot;hljs-keyword&quot;&gt;switch&lt;/span&gt; (requestCode) {
