          &lt;span class=&quot;hljs-comment&quot;&gt;// If request is cancelled, the result arrays are empty.&lt;/span&gt;
          &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (grantResults.length &amp;gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;
