        System.out.println( a1.s == a2.s ); &lt;span class=&quot;hljs-comment&quot;&gt;// true&lt;/span&gt;
        System.out.println( a1.s.equals( a2.s ) ); &lt;span class=&quot;hljs-comment&quot;&gt;// true&lt;/span&gt;

        &lt;span class=&quot;hljs-type&quot;&gt;B&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;b1&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;B&lt;/span&gt;();
