        System.out.println( b1.getS() == b2.getS() ); &lt;span class=&quot;hljs-comment&quot;&gt;// false&lt;/span&gt;
        System.out.println( b1.getS().equals( b2.getS() ) ); &lt;span class=&quot;hljs-comment&quot;&gt;// true&lt;/span&gt;
    }
}

