&lt;span class=&quot;hljs-keyword&quot;&gt;import&lt;/span&gt; java.net.*;
&lt;span class=&quot;hljs-keyword&quot;&gt;import&lt;/span&gt; java.io.*;
&lt;span class=&quot;hljs-keyword&quot;&gt;import&lt;/span&gt; java.util.*;

&lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;class&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ChessSprites&lt;/span&gt; {
    &lt;span class=&quot;hljs-keyword&quot;&gt;private&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;ChessSprites&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;()&lt;/span&gt; {}
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;int&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;SIZE&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;64&lt;/span&gt;;
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; BufferedImage SHEET;
    &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;try&lt;/span&gt; {
            &lt;span class=&quot;hljs-comment&quot;&gt;// see https://stackoverflow.com/a/19209651/2891664&lt;/span&gt;
            SHEET = ImageIO.read(&lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;URL&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;https://i.stack.imgur.com/memI0.png&quot;&lt;/span&gt;));
        } &lt;span class=&quot;hljs-keyword&quot;&gt;catch&lt;/span&gt; (IOException x) {
            &lt;span class=&quot;hljs-keyword&quot;&gt;throw&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;UncheckedIOException&lt;/span&gt;(x);
        }
    }
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;BufferedImage&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;GOLD_QUEEN&lt;/span&gt;    &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; SHEET.getSubimage(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt; * SIZE, &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,    SIZE, SIZE);
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;BufferedImage&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;SILVER_QUEEN&lt;/span&gt;  &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; SHEET.getSubimage(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt; * SIZE, SIZE, SIZE, SIZE);
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;BufferedImage&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;GOLD_KING&lt;/span&gt;     &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; SHEET.getSubimage(&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; * SIZE, &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,    SIZE, SIZE);
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;BufferedImage&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;SILVER_KING&lt;/span&gt;   &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; SHEET.getSubimage(&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; * SIZE, SIZE, SIZE, SIZE);
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;BufferedImage&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;GOLD_ROOK&lt;/span&gt;     &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; SHEET.getSubimage(&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt; * SIZE, &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,    SIZE, SIZE);
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;BufferedImage&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;SILVER_ROOK&lt;/span&gt;   &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; SHEET.getSubimage(&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt; * SIZE, SIZE, SIZE, SIZE);
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;BufferedImage&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;GOLD_KNIGHT&lt;/span&gt;   &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; SHEET.getSubimage(&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt; * SIZE, &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,    SIZE, SIZE);
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;BufferedImage&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;SILVER_KNIGHT&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; SHEET.getSubimage(&lt;span class=&quot;hljs-number&quot;&gt;3&lt;/span&gt; * SIZE, SIZE, SIZE, SIZE);
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;BufferedImage&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;GOLD_BISHOP&lt;/span&gt;   &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; SHEET.getSubimage(&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt; * SIZE, &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,    SIZE, SIZE);
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;BufferedImage&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;SILVER_BISHOP&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; SHEET.getSubimage(&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt; * SIZE, SIZE, SIZE, SIZE);
    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;static&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;final&lt;/span&gt; &lt;span class=&quot;hljs-type&quot;&gt;BufferedImage&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;GOLD_PAWN&lt;/span&gt;     &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; SHEET.getSubimage(&lt;span class=&quot;hljs-number&quot;&gt;5&lt;/span&gt; * SIZE, &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,    SIZE, SIZE);
