&lt;pre class=&quot;default s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;inArray&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;val, ary&lt;/span&gt;){
  &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; i=&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,l=ary.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt;; i&amp;lt;l; i++){
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(ary[i] === val){
      &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;;
    }
  }
  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;;
}
&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;removeClassName&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;classNameS, fromElement&lt;/span&gt;){
  &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; x = classNameS.&lt;span class=&quot;hljs-title function_&quot;&gt;split&lt;/span&gt;(&lt;span class=&quot;hljs-regexp&quot;&gt;/\s/&lt;/span&gt;), s = fromElement.&lt;span class=&quot;hljs-property&quot;&gt;className&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;split&lt;/span&gt;(&lt;span class=&quot;hljs-regexp&quot;&gt;/\s/&lt;/span&gt;), r = [];
  &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; i=&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,l=s.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt;; i&amp;lt;l; i++){
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(!&lt;span class=&quot;hljs-title function_&quot;&gt;iA&lt;/span&gt;(s[i], x))r.&lt;span class=&quot;hljs-title function_&quot;&gt;push&lt;/span&gt;(s[i]);
  }
  fromElement.&lt;span class=&quot;hljs-property&quot;&gt;className&lt;/span&gt; = r.&lt;span class=&quot;hljs-title function_&quot;&gt;join&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos; &apos;&lt;/span&gt;);
}
&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;addClassName&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;classNameS, toElement&lt;/span&gt;){
  &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; s = toElement.&lt;span class=&quot;hljs-property&quot;&gt;className&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;split&lt;/span&gt;(&lt;span class=&quot;hljs-regexp&quot;&gt;/\s/&lt;/span&gt;);
  s.&lt;span class=&quot;hljs-title function_&quot;&gt;push&lt;/span&gt;(c); toElement.&lt;span class=&quot;hljs-property&quot;&gt;className&lt;/span&gt; = s.&lt;span class=&quot;hljs-title function_&quot;&gt;join&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos; &apos;&lt;/span&gt;);
}
&lt;/code&gt;&lt;/pre&gt;

