&lt;span class=&quot;hljs-keyword&quot;&gt;FROM&lt;/span&gt; 
    ( &lt;span class=&quot;hljs-keyword&quot;&gt;SELECT&lt;/span&gt; Menu_ID, Menu_Name, Sub_Menu_ID 
