        }
        &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt;
        {
            &lt;span class=&quot;hljs-title class_&quot;&gt;Return&lt;/span&gt;_Device = &lt;span class=&quot;hljs-string&quot;&gt;&apos;Mobile&apos;&lt;/span&gt;;
        }
    }
    &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-regexp&quot;&gt;/(tablet|ipad|playbook)|(android(?!.*(mobi|opera mini)))/i&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;test&lt;/span&gt;(navigator.&lt;span class=&quot;hljs-property&quot;&gt;userAgent&lt;/span&gt;)) 
    {
        &lt;span class=&quot;hljs-title class_&quot;&gt;Return&lt;/span&gt;_Device = &lt;span class=&quot;hljs-string&quot;&gt;&apos;Tablet&apos;&lt;/span&gt;;
    }
    &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt;
    {
        &lt;span class=&quot;hljs-title class_&quot;&gt;Return&lt;/span&gt;_Device = &lt;span class=&quot;hljs-string&quot;&gt;&apos;Desktop&apos;&lt;/span&gt;;
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Return&lt;/span&gt;_Device;
}
