    -moz-box-pack: center;
    -ms-&lt;span class=&quot;hljs-attribute&quot;&gt;flex&lt;/span&gt;-pack: center;
    -webkit-&lt;span class=&quot;hljs-attribute&quot;&gt;justify-content&lt;/span&gt;: center;
    &lt;span class=&quot;hljs-attribute&quot;&gt;justify-content&lt;/span&gt;: center;
