  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;J&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;arr,id,el=&lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;&lt;/span&gt;) { 
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; cache.&lt;span class=&quot;hljs-title function_&quot;&gt;get&lt;/span&gt;(id) || (el=arr.&lt;span class=&quot;hljs-title function_&quot;&gt;find&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;o&lt;/span&gt;=&amp;gt;&lt;/span&gt; o.&lt;span class=&quot;hljs-property&quot;&gt;id&lt;/span&gt;==id), cache.&lt;span class=&quot;hljs-title function_&quot;&gt;set&lt;/span&gt;(id,el), el);
  }
})();

&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;K&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;arr, id&lt;/span&gt;) {
