myArray = [...&lt;span class=&quot;hljs-title class_&quot;&gt;Array&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;10&lt;/span&gt;)].&lt;span class=&quot;hljs-title function_&quot;&gt;map&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;x,i&lt;/span&gt;)=&amp;gt;&lt;/span&gt; ({&lt;span class=&quot;hljs-string&quot;&gt;&apos;id&apos;&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;`&lt;span class=&quot;hljs-subst&quot;&gt;${i}&lt;/span&gt;`&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;foo&apos;&lt;/span&gt;:&lt;span class=&quot;hljs-string&quot;&gt;`bar_&lt;span class=&quot;hljs-subst&quot;&gt;${i}&lt;/span&gt;`&lt;/span&gt;}));
&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; mapK = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Map&lt;/span&gt;( myArray.&lt;span class=&quot;hljs-title function_&quot;&gt;map&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;el&lt;/span&gt; =&amp;gt;&lt;/span&gt; [el.&lt;span class=&quot;hljs-property&quot;&gt;id&lt;/span&gt;, el]) );
&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; mapL = {}; myArray.&lt;span class=&quot;hljs-title function_&quot;&gt;forEach&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;el&lt;/span&gt; =&amp;gt;&lt;/span&gt; mapL[el.&lt;span class=&quot;hljs-property&quot;&gt;id&lt;/span&gt;]=el);



[A,B,C,D,E,F,G,H,I,J,K,L].&lt;span class=&quot;hljs-title function_&quot;&gt;forEach&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;f&lt;/span&gt;=&amp;gt;&lt;/span&gt; &lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;`&lt;span class=&quot;hljs-subst&quot;&gt;${f.name}&lt;/span&gt;: &lt;span class=&quot;hljs-subst&quot;&gt;${&lt;span class=&quot;hljs-built_in&quot;&gt;JSON&lt;/span&gt;.stringify(f(myArray, &lt;span class=&quot;hljs-string&quot;&gt;&apos;5&apos;&lt;/span&gt;))}&lt;/span&gt;`&lt;/span&gt;));

&lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;Whole array&apos;&lt;/span&gt;,&lt;span class=&quot;hljs-title class_&quot;&gt;JSON&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;stringify&lt;/span&gt;(myArray));&lt;/code&gt;&lt;/pre&gt;
