    &lt;span class=&quot;hljs-keyword&quot;&gt;public&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;actionPerformed&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(ActionEvent actionEvent)&lt;/span&gt; {
