        a = &lt;span class=&quot;hljs-title function_&quot;&gt;selector&lt;/span&gt;(a);
        b = &lt;span class=&quot;hljs-title function_&quot;&gt;selector&lt;/span&gt;(b);

        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (a == b) &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;;
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; (desc ? a &amp;gt; b : a &amp;lt; b) ? -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt; : &lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;;
    });
}
