          properties += property + &lt;span class=&quot;hljs-string&quot;&gt;&apos;: &apos;&lt;/span&gt; + parameters.&lt;span class=&quot;hljs-property&quot;&gt;element&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElements&lt;/span&gt;[parameters.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElement&lt;/span&gt;].&lt;span class=&quot;hljs-property&quot;&gt;properties&lt;/span&gt;[property] + &lt;span class=&quot;hljs-string&quot;&gt;&apos; !important; &apos;&lt;/span&gt;;
        };

        parameters.&lt;span class=&quot;hljs-property&quot;&gt;element&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElements&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;styleSheet&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;addRule&lt;/span&gt;(selector, properties, parameters.&lt;span class=&quot;hljs-property&quot;&gt;element&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElements&lt;/span&gt;[parameters.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElement&lt;/span&gt;].&lt;span class=&quot;hljs-property&quot;&gt;index&lt;/span&gt;);
      } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (parameters.&lt;span class=&quot;hljs-property&quot;&gt;argument&lt;/span&gt; !== &lt;span class=&quot;hljs-literal&quot;&gt;undefined&lt;/span&gt; &amp;amp;&amp;amp; parameters.&lt;span class=&quot;hljs-property&quot;&gt;property&lt;/span&gt; !== &lt;span class=&quot;hljs-literal&quot;&gt;undefined&lt;/span&gt;) {
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (!parameters.&lt;span class=&quot;hljs-property&quot;&gt;element&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElements&lt;/span&gt;[parameters.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElement&lt;/span&gt;].&lt;span class=&quot;hljs-property&quot;&gt;properties&lt;/span&gt; &amp;amp;&amp;amp; !parameters.&lt;span class=&quot;hljs-property&quot;&gt;element&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElements&lt;/span&gt;[parameters.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElement&lt;/span&gt;].&lt;span class=&quot;hljs-property&quot;&gt;index&lt;/span&gt;) {
          &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; newIndex = parameters.&lt;span class=&quot;hljs-property&quot;&gt;element&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElements&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;styleSheet&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;rules&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; || parameters.&lt;span class=&quot;hljs-property&quot;&gt;element&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElements&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;styleSheet&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;cssRules&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; || parameters.&lt;span class=&quot;hljs-property&quot;&gt;element&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElements&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;styleSheet&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt;;

          parameters.&lt;span class=&quot;hljs-property&quot;&gt;element&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElements&lt;/span&gt;[parameters.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElement&lt;/span&gt;].&lt;span class=&quot;hljs-property&quot;&gt;index&lt;/span&gt; = newIndex;
          parameters.&lt;span class=&quot;hljs-property&quot;&gt;element&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElements&lt;/span&gt;[parameters.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElement&lt;/span&gt;].&lt;span class=&quot;hljs-property&quot;&gt;properties&lt;/span&gt; = {};
        };

        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;typeof&lt;/span&gt; parameters.&lt;span class=&quot;hljs-property&quot;&gt;property&lt;/span&gt; === &lt;span class=&quot;hljs-string&quot;&gt;&apos;function&apos;&lt;/span&gt;)
          parameters.&lt;span class=&quot;hljs-property&quot;&gt;element&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElements&lt;/span&gt;[parameters.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElement&lt;/span&gt;].&lt;span class=&quot;hljs-property&quot;&gt;properties&lt;/span&gt;[parameters.&lt;span class=&quot;hljs-property&quot;&gt;argument&lt;/span&gt;] = parameters.&lt;span class=&quot;hljs-title function_&quot;&gt;property&lt;/span&gt;();
        &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt;
          parameters.&lt;span class=&quot;hljs-property&quot;&gt;element&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElements&lt;/span&gt;[parameters.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElement&lt;/span&gt;].&lt;span class=&quot;hljs-property&quot;&gt;properties&lt;/span&gt;[parameters.&lt;span class=&quot;hljs-property&quot;&gt;argument&lt;/span&gt;] = parameters.&lt;span class=&quot;hljs-property&quot;&gt;property&lt;/span&gt;;

        &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; properties = &lt;span class=&quot;hljs-string&quot;&gt;&apos;&apos;&lt;/span&gt;;

        &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; property &lt;span class=&quot;hljs-keyword&quot;&gt;in&lt;/span&gt; parameters.&lt;span class=&quot;hljs-property&quot;&gt;element&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElements&lt;/span&gt;[parameters.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElement&lt;/span&gt;].&lt;span class=&quot;hljs-property&quot;&gt;properties&lt;/span&gt;) {
          properties += property + &lt;span class=&quot;hljs-string&quot;&gt;&apos;: &apos;&lt;/span&gt; + parameters.&lt;span class=&quot;hljs-property&quot;&gt;element&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElements&lt;/span&gt;[parameters.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElement&lt;/span&gt;].&lt;span class=&quot;hljs-property&quot;&gt;properties&lt;/span&gt;[property] + &lt;span class=&quot;hljs-string&quot;&gt;&apos; !important; &apos;&lt;/span&gt;;
        };

        parameters.&lt;span class=&quot;hljs-property&quot;&gt;element&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElements&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;styleSheet&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;addRule&lt;/span&gt;(selector, properties, parameters.&lt;span class=&quot;hljs-property&quot;&gt;element&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElements&lt;/span&gt;[parameters.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElement&lt;/span&gt;].&lt;span class=&quot;hljs-property&quot;&gt;index&lt;/span&gt;);
      };
    } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (parameters.&lt;span class=&quot;hljs-property&quot;&gt;argument&lt;/span&gt; !== &lt;span class=&quot;hljs-literal&quot;&gt;undefined&lt;/span&gt; &amp;amp;&amp;amp; parameters.&lt;span class=&quot;hljs-property&quot;&gt;property&lt;/span&gt; === &lt;span class=&quot;hljs-literal&quot;&gt;undefined&lt;/span&gt;) {
      &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; windowStyle = &lt;span class=&quot;hljs-variable language_&quot;&gt;window&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;getComputedStyle&lt;/span&gt;(
        parameters.&lt;span class=&quot;hljs-property&quot;&gt;element&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;::&apos;&lt;/span&gt; + parameters.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElement&lt;/span&gt;
      ).&lt;span class=&quot;hljs-title function_&quot;&gt;getPropertyValue&lt;/span&gt;(parameters.&lt;span class=&quot;hljs-property&quot;&gt;argument&lt;/span&gt;);

      &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (parameters.&lt;span class=&quot;hljs-property&quot;&gt;element&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElements&lt;/span&gt;) {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; parameters.&lt;span class=&quot;hljs-property&quot;&gt;element&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElements&lt;/span&gt;[parameters.&lt;span class=&quot;hljs-property&quot;&gt;pseudoElement&lt;/span&gt;].&lt;span class=&quot;hljs-property&quot;&gt;properties&lt;/span&gt;[parameters.&lt;span class=&quot;hljs-property&quot;&gt;argument&lt;/span&gt;] || windowStyle;
      } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; windowStyle || &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;;
      };
    } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
      &lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;error&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;Invalid values!&apos;&lt;/span&gt;);
      &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;;
    };
  };

  &lt;span class=&quot;hljs-title class_&quot;&gt;Object&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;defineProperty&lt;/span&gt;(&lt;span class=&quot;hljs-title class_&quot;&gt;Element&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;prototype&lt;/span&gt;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;styleBefore&apos;&lt;/span&gt;, {
