    &lt;span class=&quot;hljs-attr&quot;&gt;enumerable&lt;/span&gt;: &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;,
    &lt;span class=&quot;hljs-attr&quot;&gt;value&lt;/span&gt;: &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;argument, property&lt;/span&gt;) {
      &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;setPseudoElement&lt;/span&gt;({
        &lt;span class=&quot;hljs-attr&quot;&gt;element&lt;/span&gt;: &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;,
        &lt;span class=&quot;hljs-attr&quot;&gt;pseudoElement&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;after&apos;&lt;/span&gt;,
        &lt;span class=&quot;hljs-attr&quot;&gt;argument&lt;/span&gt;: argument,
        &lt;span class=&quot;hljs-attr&quot;&gt;property&lt;/span&gt;: property
      });
    }
  });
})();

&lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;querySelector&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;.element&apos;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;styleBefore&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;content&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;&quot;New before!&quot;&apos;&lt;/span&gt;);&lt;/code&gt;&lt;/pre&gt;
&lt;pre class=&quot;snippet-code-css lang-css s-code-block&quot;&gt;&lt;code class=&quot;hljs language-css&quot;&gt;&lt;span class=&quot;hljs-selector-class&quot;&gt;.element&lt;/span&gt; {
