{
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; e;

    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; ( gRE == &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt; )
