                &lt;span class=&quot;hljs-regexp&quot;&gt;/(android|bb\d+|meego).+mobile|avantgo/&lt;/span&gt;,
                &lt;span class=&quot;hljs-regexp&quot;&gt;/bada\/|blackberry|blazer|compal|elaine|fennec|hiptop|iemobile/&lt;/span&gt;,
                &lt;span class=&quot;hljs-regexp&quot;&gt;/ip(hone|od)|iris|kindle|lge |maemo|midp|mmp|mobile.+firefox/&lt;/span&gt;,
