                &lt;span class=&quot;hljs-regexp&quot;&gt;/wap|windows ce|xda|xiino|android|ipad|playbook|silk/&lt;/span&gt;
            ];

        gRE = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;RegExp&lt;/span&gt;(
            e.&lt;span class=&quot;hljs-title function_&quot;&gt;map&lt;/span&gt;( &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt; r &lt;/span&gt;) { &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; r.&lt;span class=&quot;hljs-property&quot;&gt;source&lt;/span&gt; } ).&lt;span class=&quot;hljs-title function_&quot;&gt;join&lt;/span&gt;( &lt;span class=&quot;hljs-string&quot;&gt;&quot;|&quot;&lt;/span&gt; ), &lt;span class=&quot;hljs-string&quot;&gt;&quot;i&quot;&lt;/span&gt;
        );
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; ( gRE4 == &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt; )
    {
        e =
            [
                &lt;span class=&quot;hljs-regexp&quot;&gt;/1207|6310|6590|3gso|4thp|50[1-6]i|770s|802s|a wa/&lt;/span&gt;,
                &lt;span class=&quot;hljs-regexp&quot;&gt;/abac|ac(er|oo|s\-)|ai(ko|rn)|al(av|ca|co)|amoi|an(ex|ny|yw)/&lt;/span&gt;,
                &lt;span class=&quot;hljs-regexp&quot;&gt;/aptu|ar(ch|go)|as(te|us)|attw|au(di|\-m|r |s )|avan/&lt;/span&gt;,
