                &lt;span class=&quot;hljs-regexp&quot;&gt;/le(no|xi)|lg( g|\/(k|l|u)|50|54|\-[a-w])|libw|lynx/&lt;/span&gt;,
                &lt;span class=&quot;hljs-regexp&quot;&gt;/m1\-w|m3ga|m50\/|ma(te|ui|xo)|mc(01|21|ca)|m\-cr|me(rc|ri)/&lt;/span&gt;,
                &lt;span class=&quot;hljs-regexp&quot;&gt;/mi(o8|oa|ts)|mmef|mo(01|02|bi|de|do|t(\-| |o|v)|zz)|mt(50|p1|v )|mwbp|mywa|n10[0-2]/&lt;/span&gt;,
