                &lt;span class=&quot;hljs-regexp&quot;&gt;/p800|pan(a|d|t)|pdxg|pg(13|\-([1-8]|c))|phil|pire|pl(ay|uc)|pn\-2|po(ck|rt|se)|prox|psio|pt\-g/&lt;/span&gt;,
                &lt;span class=&quot;hljs-regexp&quot;&gt;/qa\-a|qc(07|12|21|32|60|\-[2-7]|i\-)|qtek/&lt;/span&gt;,
                &lt;span class=&quot;hljs-regexp&quot;&gt;/r380|r600|raks|rim9|ro(ve|zo)/&lt;/span&gt;,
                &lt;span class=&quot;hljs-regexp&quot;&gt;/s55\/|sa(ge|ma|mm|ms|ny|va)|sc(01|h\-|oo|p\-)|sdk\/|se(c(\-|0|1)|47|mc|nd|ri)/&lt;/span&gt;,
