        gRE4 = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;RegExp&lt;/span&gt;(
            e.&lt;span class=&quot;hljs-title function_&quot;&gt;map&lt;/span&gt;( &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt; r &lt;/span&gt;) { &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; r.&lt;span class=&quot;hljs-property&quot;&gt;source&lt;/span&gt; } ).&lt;span class=&quot;hljs-title function_&quot;&gt;join&lt;/span&gt;( &lt;span class=&quot;hljs-string&quot;&gt;&quot;|&quot;&lt;/span&gt; ), &lt;span class=&quot;hljs-string&quot;&gt;&quot;i&quot;&lt;/span&gt;
        );
