    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; key = navigator.&lt;span class=&quot;hljs-property&quot;&gt;userAgent&lt;/span&gt; || navigator.&lt;span class=&quot;hljs-property&quot;&gt;vendor&lt;/span&gt; || &lt;span class=&quot;hljs-variable language_&quot;&gt;window&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;opera&lt;/span&gt;;

    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; gRE.&lt;span class=&quot;hljs-title function_&quot;&gt;test&lt;/span&gt;( key ) ||
           gRE4.&lt;span class=&quot;hljs-title function_&quot;&gt;test&lt;/span&gt;( key.&lt;span class=&quot;hljs-title function_&quot;&gt;substr&lt;/span&gt;( &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt; ) );
