&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; isMobile = (&lt;span class=&quot;hljs-string&quot;&gt;&apos;DeviceOrientationEvent&apos;&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;in&lt;/span&gt; &lt;span class=&quot;hljs-variable language_&quot;&gt;window&lt;/span&gt; || &lt;span class=&quot;hljs-string&quot;&gt;&apos;orientation&apos;&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;in&lt;/span&gt; &lt;span class=&quot;hljs-variable language_&quot;&gt;window&lt;/span&gt;);
