&lt;p&gt;there is component ready to use (rc5 compatible)
&lt;a href=&quot;https://github.com/neuronetpl/ng2-steps&quot; rel=&quot;noreferrer&quot;&gt;ng2-steps&lt;/a&gt;
which uses &lt;code&gt;Compiler&lt;/code&gt; to inject component to step container
and service for wiring everything together (data sync)&lt;/p&gt;

&lt;pre&gt;&lt;code&gt;    import { Directive , Input, OnInit, Compiler , ViewContainerRef } from &apos;@angular/core&apos;;

import { StepsService } from &apos;./ng2-steps&apos;;

@Directive({
  selector:&apos;[ng2-step]&apos;
})
export class StepDirective implements OnInit{

  @Input(&apos;content&apos;) content:any;
  @Input(&apos;index&apos;) index:string;
  public instance;

  constructor(
    private compiler:Compiler,
    private viewContainerRef:ViewContainerRef,
    private sds:StepsService
  ){}

  ngOnInit(){
    //Magic!
    this.compiler.compileComponentAsync(this.content).then((cmpFactory)=&amp;gt;{
      const injector = this.viewContainerRef.injector;
      this.viewContainerRef.createComponent(cmpFactory, 0,  injector);
    });
  }

}
&lt;/code&gt;&lt;/pre&gt;
