    &lt;span class=&quot;hljs-attr&quot;&gt;data&lt;/span&gt;: &lt;span class=&quot;hljs-title class_&quot;&gt;JSON&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;stringify&lt;/span&gt;(json),
    &lt;span class=&quot;hljs-attr&quot;&gt;dataType&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;json&quot;&lt;/span&gt;,
    &lt;span class=&quot;hljs-attr&quot;&gt;success&lt;/span&gt;: &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;response&lt;/span&gt;){
        &lt;span class=&quot;hljs-comment&quot;&gt;// We have the response&lt;/span&gt;
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(response.&lt;span class=&quot;hljs-property&quot;&gt;status&lt;/span&gt; == &lt;span class=&quot;hljs-string&quot;&gt;&quot;SUCCESS&quot;&lt;/span&gt;){
            $(&lt;span class=&quot;hljs-string&quot;&gt;&apos;#info&apos;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;html&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Info  has been added to the list successfully.&amp;lt;br&amp;gt;&quot;&lt;/span&gt; +
            &lt;span class=&quot;hljs-string&quot;&gt;&quot;The details are as follws: &amp;lt;br&amp;gt; Name: &quot;&lt;/span&gt;);
        }
        &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt;{
            $(&lt;span class=&quot;hljs-string&quot;&gt;&apos;#info&apos;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;html&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Sorry, there is some thing wrong with the data provided.&quot;&lt;/span&gt;);
        }
    },
    &lt;span class=&quot;hljs-attr&quot;&gt;error&lt;/span&gt;: &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;e&lt;/span&gt;){
        &lt;span class=&quot;hljs-title function_&quot;&gt;alert&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;Error: &apos;&lt;/span&gt; + e);
    }
});
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Ajax (also AJAX), an acronym for Asynchronous JavaScript and XML, is a group of interrelated web development techniques used on the client-side to create asynchronous web applications. With Ajax, web applications can send data to, and retrieve data from, a server asynchronously.&lt;/p&gt;
&lt;p&gt;Below is the example code:&lt;/p&gt;
&lt;p&gt;A JSP page JavaScript function to submit data to a servlet with two variables, firstName and lastName:&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;function &lt;span class=&quot;hljs-title function_&quot;&gt;onChangeSubmitCallWebServiceAJAX&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;()&lt;/span&gt;
{
    createXmlHttpRequest();
    &lt;span class=&quot;hljs-type&quot;&gt;var&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;firstName&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; document.getElementById(&lt;span class=&quot;hljs-string&quot;&gt;&quot;firstName&quot;&lt;/span&gt;).value;
    &lt;span class=&quot;hljs-type&quot;&gt;var&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;lastName&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; document.getElementById(&lt;span class=&quot;hljs-string&quot;&gt;&quot;lastName&quot;&lt;/span&gt;).value;
    xmlHttp.open(&lt;span class=&quot;hljs-string&quot;&gt;&quot;GET&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;/AJAXServletCallSample/AjaxServlet?firstName=&quot;&lt;/span&gt;
    + firstName + &lt;span class=&quot;hljs-string&quot;&gt;&quot;&amp;amp;lastName=&quot;&lt;/span&gt; + lastName, &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;)
    xmlHttp.onreadystatechange = handleStateChange;
    xmlHttp.send(&lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;);
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;Servlet to read data send back to JSP in XML format (you could use text as well. You just need to change the response content to text and render data on JavaScript function.)&lt;/p&gt;
&lt;pre class=&quot;lang-java s-code-block&quot;&gt;&lt;code class=&quot;hljs language-java&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;/**
 * &lt;span class=&quot;hljs-doctag&quot;&gt;@see&lt;/span&gt; HttpServlet#doGet(HttpServletRequest request, HttpServletResponse response)
 */&lt;/span&gt;
&lt;span class=&quot;hljs-keyword&quot;&gt;protected&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;void&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;doGet&lt;/span&gt;&lt;span class=&quot;hljs-params&quot;&gt;(HttpServletRequest request, HttpServletResponse response)&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;throws&lt;/span&gt; ServletException, IOException {

    &lt;span class=&quot;hljs-type&quot;&gt;String&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;firstName&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; request.getParameter(&lt;span class=&quot;hljs-string&quot;&gt;&quot;firstName&quot;&lt;/span&gt;);
    &lt;span class=&quot;hljs-type&quot;&gt;String&lt;/span&gt; &lt;span class=&quot;hljs-variable&quot;&gt;lastName&lt;/span&gt; &lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt; request.getParameter(&lt;span class=&quot;hljs-string&quot;&gt;&quot;lastName&quot;&lt;/span&gt;);

    response.setContentType(&lt;span class=&quot;hljs-string&quot;&gt;&quot;text/xml&quot;&lt;/span&gt;);
    response.setHeader(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Cache-Control&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;no-cache&quot;&lt;/span&gt;);
    response.getWriter().write(&lt;span class=&quot;hljs-string&quot;&gt;&quot;&amp;lt;details&amp;gt;&quot;&lt;/span&gt;);
    response.getWriter().write(&lt;span class=&quot;hljs-string&quot;&gt;&quot;&amp;lt;firstName&amp;gt;&quot;&lt;/span&gt; + firstName + &lt;span class=&quot;hljs-string&quot;&gt;&quot;&amp;lt;/firstName&amp;gt;&quot;&lt;/span&gt;);
    response.getWriter().write(&lt;span class=&quot;hljs-string&quot;&gt;&quot;&amp;lt;lastName&amp;gt;&quot;&lt;/span&gt; + lastName + &lt;span class=&quot;hljs-string&quot;&gt;&quot;&amp;lt;/lastName&amp;gt;&quot;&lt;/span&gt;);
    response.getWriter().write(&lt;span class=&quot;hljs-string&quot;&gt;&quot;&amp;lt;/details&amp;gt;&quot;&lt;/span&gt;);
}
&lt;/code&gt;&lt;/pre&gt;

