&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;H&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;str&lt;/span&gt;)
{
    &lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; i = -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; find = &lt;span class=&quot;hljs-string&quot;&gt;&apos;abc&apos;&lt;/span&gt;;
    &lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; newToken = &lt;span class=&quot;hljs-string&quot;&gt;&apos;&apos;&lt;/span&gt;;

    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (!str)
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; ((str == &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;) &amp;amp;&amp;amp; (find == &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;)) &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; newToken;
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; str;
    }

    &lt;span class=&quot;hljs-keyword&quot;&gt;while&lt;/span&gt; ((
        i = str.&lt;span class=&quot;hljs-title function_&quot;&gt;indexOf&lt;/span&gt;(
            find, i &amp;gt;= &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt; ? i + newToken.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; : &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;
        )) !== -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;
    )
    {
        str = str.&lt;span class=&quot;hljs-title function_&quot;&gt;substring&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;, i) +
            newToken +
            str.&lt;span class=&quot;hljs-title function_&quot;&gt;substring&lt;/span&gt;(i + find.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt;);
    }
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; str;
}

&lt;span class=&quot;hljs-comment&quot;&gt;// src: https://stackoverflow.com/a/22870785/860099&lt;/span&gt;
&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;RA&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;string, prevstring&lt;/span&gt;) {
  &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; omit = &lt;span class=&quot;hljs-string&quot;&gt;&apos;abc&apos;&lt;/span&gt;;
  &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; place = &lt;span class=&quot;hljs-string&quot;&gt;&apos;&apos;&lt;/span&gt;;
  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (prevstring &amp;amp;&amp;amp; string === prevstring)
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; string;
  prevstring = string.&lt;span class=&quot;hljs-title function_&quot;&gt;replace&lt;/span&gt;(omit, place);
  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;RA&lt;/span&gt;(prevstring, string)
}

&lt;span class=&quot;hljs-comment&quot;&gt;// src: https://stackoverflow.com/a/26107132/860099&lt;/span&gt;
&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;RB&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;str&lt;/span&gt;) {
  &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; find = &lt;span class=&quot;hljs-string&quot;&gt;&apos;abc&apos;&lt;/span&gt;;
  &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; replace = &lt;span class=&quot;hljs-string&quot;&gt;&apos;&apos;&lt;/span&gt;;
  &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; i = str.&lt;span class=&quot;hljs-title function_&quot;&gt;indexOf&lt;/span&gt;(find);
  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (i &amp;gt; -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;){
    str = str.&lt;span class=&quot;hljs-title function_&quot;&gt;replace&lt;/span&gt;(find, replace);
    i = i + replace.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt;;
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; st2 = str.&lt;span class=&quot;hljs-title function_&quot;&gt;substring&lt;/span&gt;(i);
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(st2.&lt;span class=&quot;hljs-title function_&quot;&gt;indexOf&lt;/span&gt;(find) &amp;gt; -&lt;span class=&quot;hljs-number&quot;&gt;1&lt;/span&gt;){
      str = str.&lt;span class=&quot;hljs-title function_&quot;&gt;substring&lt;/span&gt;(&lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;,i) + &lt;span class=&quot;hljs-title function_&quot;&gt;RB&lt;/span&gt;(st2, find, replace);
    }
  }
  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; str;
}




