install.packages&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;&lt;span class=&quot;hljs-string&quot;&gt;&quot;reshape&quot;&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;

result&lt;span class=&quot;hljs-operator&quot;&gt;&amp;lt;-&lt;/span&gt;cast&lt;span class=&quot;hljs-punctuation&quot;&gt;(&lt;/span&gt;df&lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt; Category &lt;span class=&quot;hljs-operator&quot;&gt;~&lt;/span&gt; . &lt;span class=&quot;hljs-punctuation&quot;&gt;,&lt;/span&gt;fun.aggregate&lt;span class=&quot;hljs-operator&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;hljs-built_in&quot;&gt;sum&lt;/span&gt;&lt;span class=&quot;hljs-punctuation&quot;&gt;)&lt;/span&gt;
