&lt;p&gt;With &quot;vanilla&quot; (plain) JavaScript:&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;loadXMLDoc&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; xmlhttp = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;XMLHttpRequest&lt;/span&gt;();

    xmlhttp.&lt;span class=&quot;hljs-property&quot;&gt;onreadystatechange&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (xmlhttp.&lt;span class=&quot;hljs-property&quot;&gt;readyState&lt;/span&gt; == &lt;span class=&quot;hljs-title class_&quot;&gt;XMLHttpRequest&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;DONE&lt;/span&gt;) { &lt;span class=&quot;hljs-comment&quot;&gt;// XMLHttpRequest.DONE == 4&lt;/span&gt;
           &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (xmlhttp.&lt;span class=&quot;hljs-property&quot;&gt;status&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;200&lt;/span&gt;) {
               &lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;getElementById&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;myDiv&quot;&lt;/span&gt;).&lt;span class=&quot;hljs-property&quot;&gt;innerHTML&lt;/span&gt; = xmlhttp.&lt;span class=&quot;hljs-property&quot;&gt;responseText&lt;/span&gt;;
           }
           &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (xmlhttp.&lt;span class=&quot;hljs-property&quot;&gt;status&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;400&lt;/span&gt;) {
              &lt;span class=&quot;hljs-title function_&quot;&gt;alert&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;There was an error 400&apos;&lt;/span&gt;);
           }
           &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
               &lt;span class=&quot;hljs-title function_&quot;&gt;alert&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;something else other than 200 was returned&apos;&lt;/span&gt;);
           }
        }
    };

    xmlhttp.&lt;span class=&quot;hljs-title function_&quot;&gt;open&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;GET&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;ajax_info.txt&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);
    xmlhttp.&lt;span class=&quot;hljs-title function_&quot;&gt;send&lt;/span&gt;();
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;With &lt;a href=&quot;https://api.jquery.com/jquery.ajax/&quot; rel=&quot;nofollow noreferrer&quot;&gt;jQuery&lt;/a&gt;:&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;$.&lt;span class=&quot;hljs-title function_&quot;&gt;ajax&lt;/span&gt;({
    &lt;span class=&quot;hljs-attr&quot;&gt;url&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&quot;test.html&quot;&lt;/span&gt;,
    &lt;span class=&quot;hljs-attr&quot;&gt;context&lt;/span&gt;: &lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;body&lt;/span&gt;,
    &lt;span class=&quot;hljs-attr&quot;&gt;success&lt;/span&gt;: &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
      $(&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;addClass&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;done&quot;&lt;/span&gt;);
    }
});
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Using the following snippet you can do similar things pretty easily, like this:&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;ajax.&lt;span class=&quot;hljs-title function_&quot;&gt;get&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;/test.php&apos;&lt;/span&gt;, {&lt;span class=&quot;hljs-attr&quot;&gt;foo&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;bar&apos;&lt;/span&gt;}, &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {});
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Here is the snippet:&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; ajax = {};
ajax.&lt;span class=&quot;hljs-property&quot;&gt;x&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;typeof&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;XMLHttpRequest&lt;/span&gt; !== &lt;span class=&quot;hljs-string&quot;&gt;&apos;undefined&apos;&lt;/span&gt;) {
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;XMLHttpRequest&lt;/span&gt;();
    }
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; versions = [
        &lt;span class=&quot;hljs-string&quot;&gt;&quot;MSXML2.XmlHttp.6.0&quot;&lt;/span&gt;,
        &lt;span class=&quot;hljs-string&quot;&gt;&quot;MSXML2.XmlHttp.5.0&quot;&lt;/span&gt;,
        &lt;span class=&quot;hljs-string&quot;&gt;&quot;MSXML2.XmlHttp.4.0&quot;&lt;/span&gt;,
        &lt;span class=&quot;hljs-string&quot;&gt;&quot;MSXML2.XmlHttp.3.0&quot;&lt;/span&gt;,
        &lt;span class=&quot;hljs-string&quot;&gt;&quot;MSXML2.XmlHttp.2.0&quot;&lt;/span&gt;,
        &lt;span class=&quot;hljs-string&quot;&gt;&quot;Microsoft.XmlHttp&quot;&lt;/span&gt;
    ];

    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; xhr;
    &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; i = &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;; i &amp;lt; versions.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt;; i++) {
        &lt;span class=&quot;hljs-keyword&quot;&gt;try&lt;/span&gt; {
            xhr = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ActiveXObject&lt;/span&gt;(versions[i]);
            &lt;span class=&quot;hljs-keyword&quot;&gt;break&lt;/span&gt;;
        } &lt;span class=&quot;hljs-keyword&quot;&gt;catch&lt;/span&gt; (e) {
        }
    }
    &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; xhr;
};

ajax.&lt;span class=&quot;hljs-property&quot;&gt;send&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;url, callback, method, data, &lt;span class=&quot;hljs-keyword&quot;&gt;async&lt;/span&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;async&lt;/span&gt; === &lt;span class=&quot;hljs-literal&quot;&gt;undefined&lt;/span&gt;) {
        &lt;span class=&quot;hljs-keyword&quot;&gt;async&lt;/span&gt; = &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;;
    }
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; x = ajax.&lt;span class=&quot;hljs-title function_&quot;&gt;x&lt;/span&gt;();
    x.&lt;span class=&quot;hljs-title function_&quot;&gt;open&lt;/span&gt;(method, url, &lt;span class=&quot;hljs-keyword&quot;&gt;async&lt;/span&gt;);
    x.&lt;span class=&quot;hljs-property&quot;&gt;onreadystatechange&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (x.&lt;span class=&quot;hljs-property&quot;&gt;readyState&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;) {
            &lt;span class=&quot;hljs-title function_&quot;&gt;callback&lt;/span&gt;(x.&lt;span class=&quot;hljs-property&quot;&gt;responseText&lt;/span&gt;)
        }
    };
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (method == &lt;span class=&quot;hljs-string&quot;&gt;&apos;POST&apos;&lt;/span&gt;) {
        x.&lt;span class=&quot;hljs-title function_&quot;&gt;setRequestHeader&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;Content-type&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;application/x-www-form-urlencoded&apos;&lt;/span&gt;);
    }
    x.&lt;span class=&quot;hljs-title function_&quot;&gt;send&lt;/span&gt;(data)
};

ajax.&lt;span class=&quot;hljs-property&quot;&gt;get&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;url, data, callback, &lt;span class=&quot;hljs-keyword&quot;&gt;async&lt;/span&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; query = [];
    &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; key &lt;span class=&quot;hljs-keyword&quot;&gt;in&lt;/span&gt; data) {
        query.&lt;span class=&quot;hljs-title function_&quot;&gt;push&lt;/span&gt;(&lt;span class=&quot;hljs-built_in&quot;&gt;encodeURIComponent&lt;/span&gt;(key) + &lt;span class=&quot;hljs-string&quot;&gt;&apos;=&apos;&lt;/span&gt; + &lt;span class=&quot;hljs-built_in&quot;&gt;encodeURIComponent&lt;/span&gt;(data[key]));
    }
    ajax.&lt;span class=&quot;hljs-title function_&quot;&gt;send&lt;/span&gt;(url + (query.&lt;span class=&quot;hljs-property&quot;&gt;length&lt;/span&gt; ? &lt;span class=&quot;hljs-string&quot;&gt;&apos;?&apos;&lt;/span&gt; + query.&lt;span class=&quot;hljs-title function_&quot;&gt;join&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;amp;&apos;&lt;/span&gt;) : &lt;span class=&quot;hljs-string&quot;&gt;&apos;&apos;&lt;/span&gt;), callback, &lt;span class=&quot;hljs-string&quot;&gt;&apos;GET&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;, &lt;span class=&quot;hljs-keyword&quot;&gt;async&lt;/span&gt;)
};

ajax.&lt;span class=&quot;hljs-property&quot;&gt;post&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;url, data, callback, &lt;span class=&quot;hljs-keyword&quot;&gt;async&lt;/span&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; query = [];
    &lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; key &lt;span class=&quot;hljs-keyword&quot;&gt;in&lt;/span&gt; data) {
        query.&lt;span class=&quot;hljs-title function_&quot;&gt;push&lt;/span&gt;(&lt;span class=&quot;hljs-built_in&quot;&gt;encodeURIComponent&lt;/span&gt;(key) + &lt;span class=&quot;hljs-string&quot;&gt;&apos;=&apos;&lt;/span&gt; + &lt;span class=&quot;hljs-built_in&quot;&gt;encodeURIComponent&lt;/span&gt;(data[key]));
    }
    ajax.&lt;span class=&quot;hljs-title function_&quot;&gt;send&lt;/span&gt;(url, callback, &lt;span class=&quot;hljs-string&quot;&gt;&apos;POST&apos;&lt;/span&gt;, query.&lt;span class=&quot;hljs-title function_&quot;&gt;join&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;amp;&apos;&lt;/span&gt;), &lt;span class=&quot;hljs-keyword&quot;&gt;async&lt;/span&gt;)
};
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;There is now a nicer &lt;a href=&quot;https://developer.mozilla.org/en-US/docs/Web/API/Fetch_API&quot; rel=&quot;nofollow noreferrer&quot;&gt;Fetch API&lt;/a&gt; available natively in &lt;a href=&quot;https://caniuse.com/fetch&quot; rel=&quot;nofollow noreferrer&quot;&gt;modern browsers&lt;/a&gt;. The &lt;code&gt;fetch()&lt;/code&gt; method allows you to make web requests.
For example, to request some JSON from &lt;code&gt;/get-data&lt;/code&gt;:&lt;/p&gt;
&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;let&lt;/span&gt; options = {
  &lt;span class=&quot;hljs-attr&quot;&gt;method&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;GET&apos;&lt;/span&gt;,      
  &lt;span class=&quot;hljs-attr&quot;&gt;headers&lt;/span&gt;: {}
};

&lt;span class=&quot;hljs-title function_&quot;&gt;fetch&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;/get-data&apos;&lt;/span&gt;, options)
.&lt;span class=&quot;hljs-title function_&quot;&gt;then&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;response&lt;/span&gt; =&amp;gt;&lt;/span&gt; response.&lt;span class=&quot;hljs-title function_&quot;&gt;json&lt;/span&gt;())
.&lt;span class=&quot;hljs-title function_&quot;&gt;then&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;body&lt;/span&gt; =&amp;gt;&lt;/span&gt; {
  &lt;span class=&quot;hljs-comment&quot;&gt;// Do something with body&lt;/span&gt;
});
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;See &lt;a href=&quot;https://developer.mozilla.org/en-US/docs/Web/API/Fetch_API/Using_Fetch&quot; rel=&quot;nofollow noreferrer&quot;&gt;MDN Web Docs: Using the Fetch API&lt;/a&gt; for more details.&lt;/p&gt;

&lt;p&gt;You can use the following function:&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;callAjax&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;url, callback&lt;/span&gt;){
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; xmlhttp;
    &lt;span class=&quot;hljs-comment&quot;&gt;// compatible with IE7+, Firefox, Chrome, Opera, Safari&lt;/span&gt;
    xmlhttp = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;XMLHttpRequest&lt;/span&gt;();
    xmlhttp.&lt;span class=&quot;hljs-property&quot;&gt;onreadystatechange&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (xmlhttp.&lt;span class=&quot;hljs-property&quot;&gt;readyState&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt; &amp;amp;&amp;amp; xmlhttp.&lt;span class=&quot;hljs-property&quot;&gt;status&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;200&lt;/span&gt;){
            &lt;span class=&quot;hljs-title function_&quot;&gt;callback&lt;/span&gt;(xmlhttp.&lt;span class=&quot;hljs-property&quot;&gt;responseText&lt;/span&gt;);
        }
    }
    xmlhttp.&lt;span class=&quot;hljs-title function_&quot;&gt;open&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;GET&quot;&lt;/span&gt;, url, &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);
    xmlhttp.&lt;span class=&quot;hljs-title function_&quot;&gt;send&lt;/span&gt;();
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;You can try similar solutions online on these links:&lt;/p&gt;

&lt;ul&gt;
&lt;li&gt;&lt;a href=&quot;https://www.w3schools.com/xml/tryit.asp?filename=tryajax_first&quot; rel=&quot;noreferrer&quot;&gt;https://www.w3schools.com/xml/tryit.asp?filename=tryajax_first&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;&lt;a href=&quot;https://www.w3schools.com/xml/tryit.asp?filename=tryajax_callback&quot; rel=&quot;noreferrer&quot;&gt;https://www.w3schools.com/xml/tryit.asp?filename=tryajax_callback&lt;/a&gt;&lt;/li&gt;
&lt;/ul&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;xhttp.&lt;span class=&quot;hljs-property&quot;&gt;onreadystatechange&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;readyState&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt; &amp;amp;&amp;amp; &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;status&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;200&lt;/span&gt;) {
        &lt;span class=&quot;hljs-title function_&quot;&gt;alert&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;responseText&lt;/span&gt;);
    }
};
xhttp.&lt;span class=&quot;hljs-title function_&quot;&gt;open&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;GET&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;ajax_info.txt&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);
xhttp.&lt;span class=&quot;hljs-title function_&quot;&gt;send&lt;/span&gt;();
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;How about this version in plain &lt;strong&gt;ES6/ES2015&lt;/strong&gt;?&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;get&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;url&lt;/span&gt;) {
  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Promise&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;resolve, reject&lt;/span&gt;) =&amp;gt;&lt;/span&gt; {
    &lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; req = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;XMLHttpRequest&lt;/span&gt;();
    req.&lt;span class=&quot;hljs-title function_&quot;&gt;open&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;GET&apos;&lt;/span&gt;, url);
    req.&lt;span class=&quot;hljs-property&quot;&gt;onload&lt;/span&gt; = &lt;span class=&quot;hljs-function&quot;&gt;() =&amp;gt;&lt;/span&gt; req.&lt;span class=&quot;hljs-property&quot;&gt;status&lt;/span&gt; === &lt;span class=&quot;hljs-number&quot;&gt;200&lt;/span&gt; ? &lt;span class=&quot;hljs-title function_&quot;&gt;resolve&lt;/span&gt;(req.&lt;span class=&quot;hljs-property&quot;&gt;response&lt;/span&gt;) : &lt;span class=&quot;hljs-title function_&quot;&gt;reject&lt;/span&gt;(&lt;span class=&quot;hljs-title class_&quot;&gt;Error&lt;/span&gt;(req.&lt;span class=&quot;hljs-property&quot;&gt;statusText&lt;/span&gt;));
    req.&lt;span class=&quot;hljs-property&quot;&gt;onerror&lt;/span&gt; = &lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;e&lt;/span&gt;) =&amp;gt;&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;reject&lt;/span&gt;(&lt;span class=&quot;hljs-title class_&quot;&gt;Error&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;`Network Error: &lt;span class=&quot;hljs-subst&quot;&gt;${e}&lt;/span&gt;`&lt;/span&gt;));
    req.&lt;span class=&quot;hljs-title function_&quot;&gt;send&lt;/span&gt;();
  });
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;The function returns a &lt;a href=&quot;https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Promise&quot; rel=&quot;noreferrer&quot;&gt;promise&lt;/a&gt;. Here is an example on how to use the function and handle the &lt;em&gt;promise&lt;/em&gt; it returns:&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-title function_&quot;&gt;get&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;foo.txt&apos;&lt;/span&gt;)
.&lt;span class=&quot;hljs-title function_&quot;&gt;then&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;data&lt;/span&gt;) =&amp;gt;&lt;/span&gt; {
  &lt;span class=&quot;hljs-comment&quot;&gt;// Do stuff with data, if foo.txt was successfully loaded.&lt;/span&gt;
})
.&lt;span class=&quot;hljs-title function_&quot;&gt;catch&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;(&lt;span class=&quot;hljs-params&quot;&gt;err&lt;/span&gt;) =&amp;gt;&lt;/span&gt; {
  &lt;span class=&quot;hljs-comment&quot;&gt;// Do stuff on error...&lt;/span&gt;
});
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;If you need to load a json file you can use &lt;a href=&quot;https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/JSON/parse&quot; rel=&quot;noreferrer&quot;&gt;&lt;code&gt;JSON.parse()&lt;/code&gt;&lt;/a&gt; to convert the loaded data into an JS Object.&lt;/p&gt;

&lt;p&gt;You can also integrate &lt;a href=&quot;https://developer.mozilla.org/en-US/docs/Web/API/XMLHttpRequest/responseType&quot; rel=&quot;noreferrer&quot;&gt;&lt;code&gt;req.responseType=&apos;json&apos;&lt;/code&gt;&lt;/a&gt; into the function but unfortunately there is &lt;a href=&quot;https://developer.mozilla.org/en/docs/Web/API/XMLHttpRequest#Specifications&quot; rel=&quot;noreferrer&quot;&gt;no IE support for it&lt;/a&gt;, so I would stick with &lt;code&gt;JSON.parse()&lt;/code&gt;.&lt;/p&gt;

&lt;p&gt;Use &lt;a href=&quot;https://developer.mozilla.org/en-US/docs/Web/Guide/AJAX/Getting_Started&quot; rel=&quot;noreferrer&quot;&gt;XMLHttpRequest&lt;/a&gt;.&lt;/p&gt;

&lt;p&gt;Simple GET request&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;httpRequest = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;XMLHttpRequest&lt;/span&gt;()
httpRequest.&lt;span class=&quot;hljs-title function_&quot;&gt;open&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;GET&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;http://www.example.org/some.file&apos;&lt;/span&gt;)
httpRequest.&lt;span class=&quot;hljs-title function_&quot;&gt;send&lt;/span&gt;()
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Simple POST request&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;httpRequest = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;XMLHttpRequest&lt;/span&gt;()
httpRequest.&lt;span class=&quot;hljs-title function_&quot;&gt;open&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;POST&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;http://www.example.org/some/endpoint&apos;&lt;/span&gt;)
httpRequest.&lt;span class=&quot;hljs-title function_&quot;&gt;send&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;some data&apos;&lt;/span&gt;)
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;We can specify that the request should be asynchronous(true), the default, or synchronous(false) with an optional third argument. &lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-comment&quot;&gt;// Make a synchronous GET request&lt;/span&gt;
httpRequest.&lt;span class=&quot;hljs-title function_&quot;&gt;open&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;GET&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;http://www.example.org/some.file&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;)
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;We can set headers before calling &lt;code&gt;httpRequest.send()&lt;/code&gt;&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;httpRequest.&lt;span class=&quot;hljs-title function_&quot;&gt;setRequestHeader&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;Content-Type&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;application/x-www-form-urlencoded&apos;&lt;/span&gt;);
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;We can handle the response by setting &lt;code&gt;httpRequest.onreadystatechange&lt;/code&gt; to a function before calling &lt;code&gt;httpRequest.send()&lt;/code&gt;&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;httpRequest.&lt;span class=&quot;hljs-property&quot;&gt;onreadystatechange&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){
  &lt;span class=&quot;hljs-comment&quot;&gt;// Process the server response here.&lt;/span&gt;
  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (httpRequest.&lt;span class=&quot;hljs-property&quot;&gt;readyState&lt;/span&gt; === &lt;span class=&quot;hljs-title class_&quot;&gt;XMLHttpRequest&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;DONE&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (httpRequest.&lt;span class=&quot;hljs-property&quot;&gt;status&lt;/span&gt; === &lt;span class=&quot;hljs-number&quot;&gt;200&lt;/span&gt;) {
      &lt;span class=&quot;hljs-title function_&quot;&gt;alert&lt;/span&gt;(httpRequest.&lt;span class=&quot;hljs-property&quot;&gt;responseText&lt;/span&gt;);
    } &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
      &lt;span class=&quot;hljs-title function_&quot;&gt;alert&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;There was a problem with the request.&apos;&lt;/span&gt;);
    }
  }
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;You can get the correct object according to the browser with&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;getXmlDoc&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
  &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; xmlDoc;

  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable language_&quot;&gt;window&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;XMLHttpRequest&lt;/span&gt;) {
    &lt;span class=&quot;hljs-comment&quot;&gt;// code for IE7+, Firefox, Chrome, Opera, Safari&lt;/span&gt;
    xmlDoc = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;XMLHttpRequest&lt;/span&gt;();
  }
  &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
    &lt;span class=&quot;hljs-comment&quot;&gt;// code for IE6, IE5&lt;/span&gt;
    xmlDoc = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ActiveXObject&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Microsoft.XMLHTTP&quot;&lt;/span&gt;);
  }

  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; xmlDoc;
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;With the correct object, a GET might can be abstracted to:&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;myGet&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;url, callback&lt;/span&gt;) {
  &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; xmlDoc = &lt;span class=&quot;hljs-title function_&quot;&gt;getXmlDoc&lt;/span&gt;();

  xmlDoc.&lt;span class=&quot;hljs-title function_&quot;&gt;open&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;GET&apos;&lt;/span&gt;, url, &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);

  xmlDoc.&lt;span class=&quot;hljs-property&quot;&gt;onreadystatechange&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (xmlDoc.&lt;span class=&quot;hljs-property&quot;&gt;readyState&lt;/span&gt; === &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt; &amp;amp;&amp;amp; xmlDoc.&lt;span class=&quot;hljs-property&quot;&gt;status&lt;/span&gt; === &lt;span class=&quot;hljs-number&quot;&gt;200&lt;/span&gt;) {
      &lt;span class=&quot;hljs-title function_&quot;&gt;callback&lt;/span&gt;(xmlDoc);
    }
  }

  xmlDoc.&lt;span class=&quot;hljs-title function_&quot;&gt;send&lt;/span&gt;();
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;And a POST to:&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;myPost&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;url, data, callback&lt;/span&gt;) {
  &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; xmlDoc = &lt;span class=&quot;hljs-title function_&quot;&gt;getXmlDoc&lt;/span&gt;();

  xmlDoc.&lt;span class=&quot;hljs-title function_&quot;&gt;open&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;POST&apos;&lt;/span&gt;, url, &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);
  xmlDoc.&lt;span class=&quot;hljs-title function_&quot;&gt;setRequestHeader&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Content-type&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;application/x-www-form-urlencoded&quot;&lt;/span&gt;);

  xmlDoc.&lt;span class=&quot;hljs-property&quot;&gt;onreadystatechange&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (xmlDoc.&lt;span class=&quot;hljs-property&quot;&gt;readyState&lt;/span&gt; === &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt; &amp;amp;&amp;amp; xmlDoc.&lt;span class=&quot;hljs-property&quot;&gt;status&lt;/span&gt; === &lt;span class=&quot;hljs-number&quot;&gt;200&lt;/span&gt;) {
      &lt;span class=&quot;hljs-title function_&quot;&gt;callback&lt;/span&gt;(xmlDoc);
    }
  }

  xmlDoc.&lt;span class=&quot;hljs-title function_&quot;&gt;send&lt;/span&gt;(data);
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;I was looking for a way to include promises with ajax and exclude jQuery. There&apos;s an article on &lt;a href=&quot;http://www.html5rocks.com/en/tutorials/es6/promises/&quot; rel=&quot;nofollow noreferrer&quot;&gt;HTML5 Rocks&lt;/a&gt; that talks about ES6 promises. (You could polyfill with a promise library like &lt;a href=&quot;https://github.com/kriskowal/q&quot; rel=&quot;nofollow noreferrer&quot;&gt;Q&lt;/a&gt;) You can use the code snippet that I copied from the article.&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;get&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;url&lt;/span&gt;) {
  &lt;span class=&quot;hljs-comment&quot;&gt;// Return a new promise.&lt;/span&gt;
  &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;Promise&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;resolve, reject&lt;/span&gt;) {
    &lt;span class=&quot;hljs-comment&quot;&gt;// Do the usual XHR stuff&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; req = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;XMLHttpRequest&lt;/span&gt;();
    req.&lt;span class=&quot;hljs-title function_&quot;&gt;open&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;GET&apos;&lt;/span&gt;, url);

    req.&lt;span class=&quot;hljs-property&quot;&gt;onload&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
      &lt;span class=&quot;hljs-comment&quot;&gt;// This is called even on 404 etc&lt;/span&gt;
      &lt;span class=&quot;hljs-comment&quot;&gt;// so check the status&lt;/span&gt;
      &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (req.&lt;span class=&quot;hljs-property&quot;&gt;status&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;200&lt;/span&gt;) {
        &lt;span class=&quot;hljs-comment&quot;&gt;// Resolve the promise with the response text&lt;/span&gt;
        &lt;span class=&quot;hljs-title function_&quot;&gt;resolve&lt;/span&gt;(req.&lt;span class=&quot;hljs-property&quot;&gt;response&lt;/span&gt;);
      }
      &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
        &lt;span class=&quot;hljs-comment&quot;&gt;// Otherwise reject with the status text&lt;/span&gt;
        &lt;span class=&quot;hljs-comment&quot;&gt;// which will hopefully be a meaningful error&lt;/span&gt;
        &lt;span class=&quot;hljs-title function_&quot;&gt;reject&lt;/span&gt;(&lt;span class=&quot;hljs-title class_&quot;&gt;Error&lt;/span&gt;(req.&lt;span class=&quot;hljs-property&quot;&gt;statusText&lt;/span&gt;));
      }
    };

    &lt;span class=&quot;hljs-comment&quot;&gt;// Handle network errors&lt;/span&gt;
    req.&lt;span class=&quot;hljs-property&quot;&gt;onerror&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
      &lt;span class=&quot;hljs-title function_&quot;&gt;reject&lt;/span&gt;(&lt;span class=&quot;hljs-title class_&quot;&gt;Error&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Network Error&quot;&lt;/span&gt;));
    };

    &lt;span class=&quot;hljs-comment&quot;&gt;// Make the request&lt;/span&gt;
    req.&lt;span class=&quot;hljs-title function_&quot;&gt;send&lt;/span&gt;();
  });
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Note: I also wrote &lt;a href=&quot;https://www.aligneddev.net/blog/2020/ajaxpromiseswithoutjquery/&quot; rel=&quot;nofollow noreferrer&quot;&gt;an article about this&lt;/a&gt;.&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;A small combination from a couple of the examples below and created this simple piece:&lt;/strong&gt;&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;ajax&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;url, method, data, &lt;span class=&quot;hljs-keyword&quot;&gt;async&lt;/span&gt;&lt;/span&gt;)
{
    method = &lt;span class=&quot;hljs-keyword&quot;&gt;typeof&lt;/span&gt; method !== &lt;span class=&quot;hljs-string&quot;&gt;&apos;undefined&apos;&lt;/span&gt; ? method : &lt;span class=&quot;hljs-string&quot;&gt;&apos;GET&apos;&lt;/span&gt;;
    &lt;span class=&quot;hljs-keyword&quot;&gt;async&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;typeof&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;async&lt;/span&gt; !== &lt;span class=&quot;hljs-string&quot;&gt;&apos;undefined&apos;&lt;/span&gt; ? &lt;span class=&quot;hljs-keyword&quot;&gt;async&lt;/span&gt; : &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;;

    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable language_&quot;&gt;window&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;XMLHttpRequest&lt;/span&gt;)
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; xhReq = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;XMLHttpRequest&lt;/span&gt;();
    }
    &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt;
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; xhReq = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ActiveXObject&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Microsoft.XMLHTTP&quot;&lt;/span&gt;);
    }


    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (method == &lt;span class=&quot;hljs-string&quot;&gt;&apos;POST&apos;&lt;/span&gt;)
    {
        xhReq.&lt;span class=&quot;hljs-title function_&quot;&gt;open&lt;/span&gt;(method, url, &lt;span class=&quot;hljs-keyword&quot;&gt;async&lt;/span&gt;);
        xhReq.&lt;span class=&quot;hljs-title function_&quot;&gt;setRequestHeader&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Content-type&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;application/x-www-form-urlencoded&quot;&lt;/span&gt;);
        xhReq.&lt;span class=&quot;hljs-title function_&quot;&gt;setRequestHeader&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;X-Requested-With&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;XMLHttpRequest&quot;&lt;/span&gt;);
        xhReq.&lt;span class=&quot;hljs-title function_&quot;&gt;send&lt;/span&gt;(data);
    }
    &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt;
    {
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-keyword&quot;&gt;typeof&lt;/span&gt; data !== &lt;span class=&quot;hljs-string&quot;&gt;&apos;undefined&apos;&lt;/span&gt; &amp;amp;&amp;amp; data !== &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;)
        {
            url = url+&lt;span class=&quot;hljs-string&quot;&gt;&apos;?&apos;&lt;/span&gt;+data;
        }
        xhReq.&lt;span class=&quot;hljs-title function_&quot;&gt;open&lt;/span&gt;(method, url, &lt;span class=&quot;hljs-keyword&quot;&gt;async&lt;/span&gt;);
        xhReq.&lt;span class=&quot;hljs-title function_&quot;&gt;setRequestHeader&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;X-Requested-With&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;XMLHttpRequest&quot;&lt;/span&gt;);
        xhReq.&lt;span class=&quot;hljs-title function_&quot;&gt;send&lt;/span&gt;(&lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;);
    }
    &lt;span class=&quot;hljs-comment&quot;&gt;//var serverResponse = xhReq.responseText;&lt;/span&gt;
    &lt;span class=&quot;hljs-comment&quot;&gt;//alert(serverResponse);&lt;/span&gt;
}

&lt;span class=&quot;hljs-comment&quot;&gt;// Example usage below (using a string query):&lt;/span&gt;

&lt;span class=&quot;hljs-title function_&quot;&gt;ajax&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;http://www.google.com&apos;&lt;/span&gt;);
&lt;span class=&quot;hljs-title function_&quot;&gt;ajax&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;http://www.google.com&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;POST&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;q=test&apos;&lt;/span&gt;);
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;&lt;strong&gt;OR if your parameters are object(s) - minor additional code adjustment:&lt;/strong&gt;&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; parameters = {
    &lt;span class=&quot;hljs-attr&quot;&gt;q&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;test&apos;&lt;/span&gt;
}

&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; query = [];
&lt;span class=&quot;hljs-keyword&quot;&gt;for&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; key &lt;span class=&quot;hljs-keyword&quot;&gt;in&lt;/span&gt; parameters)
{
    query.&lt;span class=&quot;hljs-title function_&quot;&gt;push&lt;/span&gt;(&lt;span class=&quot;hljs-built_in&quot;&gt;encodeURIComponent&lt;/span&gt;(key) + &lt;span class=&quot;hljs-string&quot;&gt;&apos;=&apos;&lt;/span&gt; + &lt;span class=&quot;hljs-built_in&quot;&gt;encodeURIComponent&lt;/span&gt;(parameters[key]));
}

&lt;span class=&quot;hljs-title function_&quot;&gt;ajax&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;http://www.google.com&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;POST&apos;&lt;/span&gt;, query.&lt;span class=&quot;hljs-title function_&quot;&gt;join&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;amp;&apos;&lt;/span&gt;));
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Both should be fully browser + version compatible.&lt;/p&gt;

&lt;p&gt;If you don&apos;t want to include JQuery, I&apos;d try out some lightweight AJAX libraries.&lt;/p&gt;

&lt;p&gt;My favorite is reqwest. It&apos;s only 3.4kb and very well built out: &lt;a href=&quot;https://github.com/ded/Reqwest&quot; rel=&quot;noreferrer&quot;&gt;https://github.com/ded/Reqwest&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;Here&apos;s a sample GET request with reqwest:&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-title function_&quot;&gt;reqwest&lt;/span&gt;({
    &lt;span class=&quot;hljs-attr&quot;&gt;url&lt;/span&gt;: url,
    &lt;span class=&quot;hljs-attr&quot;&gt;method&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;GET&apos;&lt;/span&gt;,
    &lt;span class=&quot;hljs-attr&quot;&gt;type&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;json&apos;&lt;/span&gt;,
    &lt;span class=&quot;hljs-attr&quot;&gt;success&lt;/span&gt;: onSuccess
});
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Now if you want something even more lightweight, I&apos;d try microAjax at a mere 0.4kb: &lt;a href=&quot;https://code.google.com/p/microajax/&quot; rel=&quot;noreferrer&quot;&gt;https://code.google.com/p/microajax/&lt;/a&gt;&lt;/p&gt;

&lt;p&gt;This is all the code right here:&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;microAjax&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;B,A&lt;/span&gt;){&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;bindFunction&lt;/span&gt;=&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;E,D&lt;/span&gt;){&lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){&lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; E.&lt;span class=&quot;hljs-title function_&quot;&gt;apply&lt;/span&gt;(D,[D])}};&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;stateChange&lt;/span&gt;=&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;D&lt;/span&gt;){&lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;request&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;readyState&lt;/span&gt;==&lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;){&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;callbackFunction&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;request&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;responseText&lt;/span&gt;)}};&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;getRequest&lt;/span&gt;=&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;){&lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;window&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;ActiveXObject&lt;/span&gt;){&lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ActiveXObject&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Microsoft.XMLHTTP&quot;&lt;/span&gt;)}&lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt;{&lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;window&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;XMLHttpRequest&lt;/span&gt;){&lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;XMLHttpRequest&lt;/span&gt;()}}&lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;};&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;postBody&lt;/span&gt;=(&lt;span class=&quot;hljs-variable language_&quot;&gt;arguments&lt;/span&gt;[&lt;span class=&quot;hljs-number&quot;&gt;2&lt;/span&gt;]||&lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&lt;/span&gt;);&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;callbackFunction&lt;/span&gt;=A;&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;url&lt;/span&gt;=B;&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;request&lt;/span&gt;=&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;getRequest&lt;/span&gt;();&lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;request&lt;/span&gt;){&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; C=&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;request&lt;/span&gt;;C.&lt;span class=&quot;hljs-property&quot;&gt;onreadystatechange&lt;/span&gt;=&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;bindFunction&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;stateChange&lt;/span&gt;,&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;);&lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;postBody&lt;/span&gt;!==&lt;span class=&quot;hljs-string&quot;&gt;&quot;&quot;&lt;/span&gt;){C.&lt;span class=&quot;hljs-title function_&quot;&gt;open&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;POST&quot;&lt;/span&gt;,B,&lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);C.&lt;span class=&quot;hljs-title function_&quot;&gt;setRequestHeader&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;X-Requested-With&quot;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&quot;XMLHttpRequest&quot;&lt;/span&gt;);C.&lt;span class=&quot;hljs-title function_&quot;&gt;setRequestHeader&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Content-type&quot;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&quot;application/x-www-form-urlencoded&quot;&lt;/span&gt;);C.&lt;span class=&quot;hljs-title function_&quot;&gt;setRequestHeader&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Connection&quot;&lt;/span&gt;,&lt;span class=&quot;hljs-string&quot;&gt;&quot;close&quot;&lt;/span&gt;)}&lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt;{C.&lt;span class=&quot;hljs-title function_&quot;&gt;open&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;GET&quot;&lt;/span&gt;,B,&lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;)}C.&lt;span class=&quot;hljs-title function_&quot;&gt;send&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;postBody&lt;/span&gt;)}};
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;And here&apos;s a sample call:&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-title function_&quot;&gt;microAjax&lt;/span&gt;(url, onSuccess);
&lt;/code&gt;&lt;/pre&gt;

&lt;h2&gt;XMLHttpRequest()&lt;/h2&gt;

&lt;p&gt;You can use the &lt;a href=&quot;https://developer.mozilla.org/en-US/docs/Web/API/XMLHttpRequest/XMLHttpRequest&quot; rel=&quot;noreferrer&quot;&gt;&lt;code&gt;XMLHttpRequest()&lt;/code&gt;&lt;/a&gt; constructor to create a new &lt;a href=&quot;https://developer.mozilla.org/en-US/docs/Web/API/XMLHttpRequest&quot; rel=&quot;noreferrer&quot;&gt;&lt;code&gt;XMLHttpRequest&lt;/code&gt;&lt;/a&gt; (XHR) object which will allow you to interact with a server using standard HTTP request methods (such as &lt;a href=&quot;https://developer.mozilla.org/en-US/docs/Web/HTTP/Methods/GET&quot; rel=&quot;noreferrer&quot;&gt;&lt;code&gt;GET&lt;/code&gt;&lt;/a&gt; and &lt;a href=&quot;https://developer.mozilla.org/en-US/docs/Web/HTTP/Methods/POST&quot; rel=&quot;noreferrer&quot;&gt;&lt;code&gt;POST&lt;/code&gt;&lt;/a&gt;):&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; data = &lt;span class=&quot;hljs-title class_&quot;&gt;JSON&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;stringify&lt;/span&gt;({
  &lt;span class=&quot;hljs-attr&quot;&gt;example_1&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;123&lt;/span&gt;,
  &lt;span class=&quot;hljs-attr&quot;&gt;example_2&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;Hello, world!&apos;&lt;/span&gt;,
});

&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; request = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;XMLHttpRequest&lt;/span&gt;();

request.&lt;span class=&quot;hljs-title function_&quot;&gt;addEventListener&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;load&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;readyState&lt;/span&gt; === &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt; &amp;amp;&amp;amp; &lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;status&lt;/span&gt; === &lt;span class=&quot;hljs-number&quot;&gt;200&lt;/span&gt;) {
    &lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;responseText&lt;/span&gt;);
  }
});

request.&lt;span class=&quot;hljs-title function_&quot;&gt;open&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;POST&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;example.php&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);
request.&lt;span class=&quot;hljs-title function_&quot;&gt;setRequestHeader&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;Content-Type&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;application/x-www-form-urlencoded; charset=UTF-8&apos;&lt;/span&gt;);
request.&lt;span class=&quot;hljs-title function_&quot;&gt;send&lt;/span&gt;(data);
&lt;/code&gt;&lt;/pre&gt;

&lt;h2&gt;fetch()&lt;/h2&gt;

&lt;p&gt;You can also use the &lt;a href=&quot;https://developer.mozilla.org/en-US/docs/Web/API/WindowOrWorkerGlobalScope/fetch&quot; rel=&quot;noreferrer&quot;&gt;&lt;code&gt;fetch()&lt;/code&gt;&lt;/a&gt; method to obtain a &lt;a href=&quot;https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Promise&quot; rel=&quot;noreferrer&quot;&gt;&lt;code&gt;Promise&lt;/code&gt;&lt;/a&gt; which resolves to the &lt;a href=&quot;https://developer.mozilla.org/en-US/docs/Web/API/Response&quot; rel=&quot;noreferrer&quot;&gt;&lt;code&gt;Response&lt;/code&gt;&lt;/a&gt; object representing the response to your request:&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; data = &lt;span class=&quot;hljs-title class_&quot;&gt;JSON&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;stringify&lt;/span&gt;({
  &lt;span class=&quot;hljs-attr&quot;&gt;example_1&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;123&lt;/span&gt;,
  &lt;span class=&quot;hljs-attr&quot;&gt;example_2&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;Hello, world!&apos;&lt;/span&gt;,
});

&lt;span class=&quot;hljs-title function_&quot;&gt;fetch&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;example.php&apos;&lt;/span&gt;, {
  &lt;span class=&quot;hljs-attr&quot;&gt;method&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;POST&apos;&lt;/span&gt;,
  &lt;span class=&quot;hljs-attr&quot;&gt;headers&lt;/span&gt;: {
    &lt;span class=&quot;hljs-string&quot;&gt;&apos;Content-Type&apos;&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;application/x-www-form-urlencoded; charset=UTF-8&apos;&lt;/span&gt;,
  },
  &lt;span class=&quot;hljs-attr&quot;&gt;body&lt;/span&gt;: data,
}).&lt;span class=&quot;hljs-title function_&quot;&gt;then&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;response&lt;/span&gt; =&amp;gt;&lt;/span&gt; {
  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (response.&lt;span class=&quot;hljs-property&quot;&gt;ok&lt;/span&gt;) {
    response.&lt;span class=&quot;hljs-title function_&quot;&gt;text&lt;/span&gt;().&lt;span class=&quot;hljs-title function_&quot;&gt;then&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;response&lt;/span&gt; =&amp;gt;&lt;/span&gt; {
      &lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(response);
    });
  }
});
&lt;/code&gt;&lt;/pre&gt;

&lt;h2&gt;navigator.sendBeacon()&lt;/h2&gt;

&lt;p&gt;On the other hand, if you are simply attempting to &lt;a href=&quot;https://developer.mozilla.org/en-US/docs/Web/HTTP/Methods/POST&quot; rel=&quot;noreferrer&quot;&gt;&lt;code&gt;POST&lt;/code&gt;&lt;/a&gt; data and do not need a response from the server, the shortest solution would be to use &lt;a href=&quot;https://developer.mozilla.org/en-US/docs/Web/API/Navigator/sendBeacon&quot; rel=&quot;noreferrer&quot;&gt;&lt;code&gt;navigator.sendBeacon()&lt;/code&gt;&lt;/a&gt;:&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; data = &lt;span class=&quot;hljs-title class_&quot;&gt;JSON&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;stringify&lt;/span&gt;({
  &lt;span class=&quot;hljs-attr&quot;&gt;example_1&lt;/span&gt;: &lt;span class=&quot;hljs-number&quot;&gt;123&lt;/span&gt;,
  &lt;span class=&quot;hljs-attr&quot;&gt;example_2&lt;/span&gt;: &lt;span class=&quot;hljs-string&quot;&gt;&apos;Hello, world!&apos;&lt;/span&gt;,
});

navigator.&lt;span class=&quot;hljs-title function_&quot;&gt;sendBeacon&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;example.php&apos;&lt;/span&gt;, data);
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Old but I will try, maybe someone will find this info useful.&lt;/p&gt;

&lt;p&gt;This is the minimal amount of code you need to do a &lt;code&gt;GET&lt;/code&gt; request and fetch some &lt;code&gt;JSON&lt;/code&gt; formatted data. This is applicable only to modern browsers like latest versions of &lt;strong&gt;Chrome&lt;/strong&gt;, &lt;strong&gt;FF&lt;/strong&gt;, &lt;strong&gt;Safari&lt;/strong&gt;, &lt;strong&gt;Opera&lt;/strong&gt; and &lt;strong&gt;Microsoft Edge&lt;/strong&gt;.&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;const&lt;/span&gt; xhr = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;XMLHttpRequest&lt;/span&gt;();
xhr.&lt;span class=&quot;hljs-title function_&quot;&gt;open&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;GET&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;https://example.com/data.json&apos;&lt;/span&gt;); &lt;span class=&quot;hljs-comment&quot;&gt;// by default async &lt;/span&gt;
xhr.&lt;span class=&quot;hljs-property&quot;&gt;responseType&lt;/span&gt; = &lt;span class=&quot;hljs-string&quot;&gt;&apos;json&apos;&lt;/span&gt;; &lt;span class=&quot;hljs-comment&quot;&gt;// in which format you expect the response to be&lt;/span&gt;


xhr.&lt;span class=&quot;hljs-property&quot;&gt;onload&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
  &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;status&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;200&lt;/span&gt;) {&lt;span class=&quot;hljs-comment&quot;&gt;// onload called even on 404 etc so check the status&lt;/span&gt;
   &lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;this&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;response&lt;/span&gt;); &lt;span class=&quot;hljs-comment&quot;&gt;// No need for JSON.parse()&lt;/span&gt;
  }
};

xhr.&lt;span class=&quot;hljs-property&quot;&gt;onerror&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
  &lt;span class=&quot;hljs-comment&quot;&gt;// error &lt;/span&gt;
};


xhr.&lt;span class=&quot;hljs-title function_&quot;&gt;send&lt;/span&gt;();
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Also check out new &lt;a href=&quot;https://fetch.spec.whatwg.org/&quot;&gt;&lt;strong&gt;Fetch API&lt;/strong&gt;&lt;/a&gt; which is a promise-based replacement for &lt;a href=&quot;https://xhr.spec.whatwg.org/&quot;&gt;&lt;strong&gt;XMLHttpRequest API&lt;/strong&gt;&lt;/a&gt;. &lt;/p&gt;

&lt;p&gt;Try using the Fetch Api (&lt;a href=&quot;https://developer.mozilla.org/en-US/docs/Web/API/Fetch_API/Using_Fetch&quot; rel=&quot;noreferrer&quot;&gt;Fetch API&lt;/a&gt;)&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-title function_&quot;&gt;fetch&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;http://example.com/movies.json&apos;&lt;/span&gt;).&lt;span class=&quot;hljs-title function_&quot;&gt;then&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;response&lt;/span&gt; =&amp;gt;&lt;/span&gt; response.&lt;span class=&quot;hljs-title function_&quot;&gt;json&lt;/span&gt;()).&lt;span class=&quot;hljs-title function_&quot;&gt;then&lt;/span&gt;(&lt;span class=&quot;hljs-function&quot;&gt;&lt;span class=&quot;hljs-params&quot;&gt;data&lt;/span&gt; =&amp;gt;&lt;/span&gt; &lt;span class=&quot;hljs-variable language_&quot;&gt;console&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;log&lt;/span&gt;(data));
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Its really clear, and 100% vanilla.&lt;/p&gt;

&lt;p&gt;From &lt;a href=&quot;http://youmightnotneedjquery.com/#post&quot; rel=&quot;noreferrer&quot;&gt;youMightNotNeedJquery.com&lt;/a&gt; + &lt;code&gt;JSON.stringify&lt;/code&gt;&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; request = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;XMLHttpRequest&lt;/span&gt;();
request.&lt;span class=&quot;hljs-title function_&quot;&gt;open&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;POST&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;/my/url&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);
request.&lt;span class=&quot;hljs-title function_&quot;&gt;setRequestHeader&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;Content-Type&apos;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&apos;application/x-www-form-urlencoded; charset=UTF-8&apos;&lt;/span&gt;);
request.&lt;span class=&quot;hljs-title function_&quot;&gt;send&lt;/span&gt;(&lt;span class=&quot;hljs-title class_&quot;&gt;JSON&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;stringify&lt;/span&gt;(data));
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;This may help:&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;doAjax&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;url, callback&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; xmlhttp = &lt;span class=&quot;hljs-variable language_&quot;&gt;window&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;XMLHttpRequest&lt;/span&gt; ? &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;XMLHttpRequest&lt;/span&gt;() : &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ActiveXObject&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Microsoft.XMLHTTP&quot;&lt;/span&gt;);

    xmlhttp.&lt;span class=&quot;hljs-property&quot;&gt;onreadystatechange&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (xmlhttp.&lt;span class=&quot;hljs-property&quot;&gt;readyState&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt; &amp;amp;&amp;amp; xmlhttp.&lt;span class=&quot;hljs-property&quot;&gt;status&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;200&lt;/span&gt;) {
            &lt;span class=&quot;hljs-title function_&quot;&gt;callback&lt;/span&gt;(xmlhttp.&lt;span class=&quot;hljs-property&quot;&gt;responseText&lt;/span&gt;);
        }
    }

    xmlhttp.&lt;span class=&quot;hljs-title function_&quot;&gt;open&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;GET&quot;&lt;/span&gt;, url, &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);
    xmlhttp.&lt;span class=&quot;hljs-title function_&quot;&gt;send&lt;/span&gt;();
}
&lt;/code&gt;&lt;/pre&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&amp;lt;html&amp;gt;
  &lt;span class=&quot;language-xml&quot;&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;script&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;span class=&quot;language-javascript&quot;&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; xmlDoc = &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt; ;

  &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;load&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-keyword&quot;&gt;typeof&lt;/span&gt; &lt;span class=&quot;hljs-variable language_&quot;&gt;window&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;ActiveXObject&lt;/span&gt; != &lt;span class=&quot;hljs-string&quot;&gt;&apos;undefined&apos;&lt;/span&gt; ) {
      xmlDoc = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ActiveXObject&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Microsoft.XMLHTTP&quot;&lt;/span&gt;);
      xmlDoc.&lt;span class=&quot;hljs-property&quot;&gt;onreadystatechange&lt;/span&gt; = process ;
    }
    &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
      xmlDoc = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;XMLHttpRequest&lt;/span&gt;();
      xmlDoc.&lt;span class=&quot;hljs-property&quot;&gt;onload&lt;/span&gt; = process ;
    }
    xmlDoc.&lt;span class=&quot;hljs-title function_&quot;&gt;open&lt;/span&gt;( &lt;span class=&quot;hljs-string&quot;&gt;&quot;GET&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;background.html&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt; );
    xmlDoc.&lt;span class=&quot;hljs-title function_&quot;&gt;send&lt;/span&gt;( &lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt; );
  }

  &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;process&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; ( xmlDoc.&lt;span class=&quot;hljs-property&quot;&gt;readyState&lt;/span&gt; != &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt; ) &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; ;
    &lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;getElementById&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;output&quot;&lt;/span&gt;).&lt;span class=&quot;hljs-property&quot;&gt;value&lt;/span&gt; = xmlDoc.&lt;span class=&quot;hljs-property&quot;&gt;responseText&lt;/span&gt; ;
  }

  &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;empty&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;getElementById&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;output&quot;&lt;/span&gt;).&lt;span class=&quot;hljs-property&quot;&gt;value&lt;/span&gt; = &lt;span class=&quot;hljs-string&quot;&gt;&apos;&amp;lt;empty&amp;gt;&apos;&lt;/span&gt; ;
  }
&lt;/span&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;script&lt;/span&gt;&amp;gt;&lt;/span&gt;

&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;body&lt;/span&gt;&amp;gt;&lt;/span&gt;
  &lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;textarea&lt;/span&gt; &lt;span class=&quot;hljs-attr&quot;&gt;id&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&quot;output&quot;&lt;/span&gt; &lt;span class=&quot;hljs-attr&quot;&gt;cols&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&apos;70&apos;&lt;/span&gt; &lt;span class=&quot;hljs-attr&quot;&gt;rows&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&apos;40&apos;&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;empty&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;textarea&lt;/span&gt;&amp;gt;&lt;/span&gt;
  &lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;br&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;br&lt;/span&gt;&amp;gt;&lt;/span&gt;
  &lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;button&lt;/span&gt; &lt;span class=&quot;hljs-attr&quot;&gt;onclick&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&quot;load()&quot;&lt;/span&gt;&amp;gt;&lt;/span&gt;Load&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;button&lt;/span&gt;&amp;gt;&lt;/span&gt; &lt;span class=&quot;hljs-symbol&quot;&gt;&amp;amp;nbsp;&lt;/span&gt;
  &lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;&lt;span class=&quot;hljs-name&quot;&gt;button&lt;/span&gt; &lt;span class=&quot;hljs-attr&quot;&gt;onclick&lt;/span&gt;=&lt;span class=&quot;hljs-string&quot;&gt;&quot;empty()&quot;&lt;/span&gt;&amp;gt;&lt;/span&gt;Clear&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;button&lt;/span&gt;&amp;gt;&lt;/span&gt;
&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;body&lt;/span&gt;&amp;gt;&lt;/span&gt;
&lt;span class=&quot;hljs-tag&quot;&gt;&amp;lt;/&lt;span class=&quot;hljs-name&quot;&gt;html&lt;/span&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;

&lt;blockquote&gt;
  &lt;p&gt;Well it is just a 4 step easy proceess,&lt;/p&gt;
&lt;/blockquote&gt;

&lt;p&gt;I hope it helps&lt;/p&gt;

&lt;p&gt;&lt;code&gt;Step 1.&lt;/code&gt; Store the reference to the XMLHttpRequest object&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; xmlHttp = &lt;span class=&quot;hljs-title function_&quot;&gt;createXmlHttpRequestObject&lt;/span&gt;();
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;&lt;code&gt;Step 2.&lt;/code&gt; Retrieve the XMLHttpRequest object&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;createXmlHttpRequestObject&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-comment&quot;&gt;// will store the reference to the XMLHttpRequest object&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; xmlHttp;
    &lt;span class=&quot;hljs-comment&quot;&gt;// if running Internet Explorer&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (&lt;span class=&quot;hljs-variable language_&quot;&gt;window&lt;/span&gt;.&lt;span class=&quot;hljs-property&quot;&gt;ActiveXObject&lt;/span&gt;) {
        &lt;span class=&quot;hljs-keyword&quot;&gt;try&lt;/span&gt; {
            xmlHttp = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;ActiveXObject&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Microsoft.XMLHTTP&quot;&lt;/span&gt;);
        } &lt;span class=&quot;hljs-keyword&quot;&gt;catch&lt;/span&gt; (e) {
            xmlHttp = &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;;
        }
    }
    &lt;span class=&quot;hljs-comment&quot;&gt;// if running Mozilla or other browsers&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
        &lt;span class=&quot;hljs-keyword&quot;&gt;try&lt;/span&gt; {
            xmlHttp = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;XMLHttpRequest&lt;/span&gt;();
        } &lt;span class=&quot;hljs-keyword&quot;&gt;catch&lt;/span&gt; (e) {
            xmlHttp = &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;;
        }
    }
    &lt;span class=&quot;hljs-comment&quot;&gt;// return the created object or display an error message&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (!xmlHttp)
        &lt;span class=&quot;hljs-title function_&quot;&gt;alert&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;Error creating the XMLHttpRequest object.&quot;&lt;/span&gt;);
    &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt;
        &lt;span class=&quot;hljs-keyword&quot;&gt;return&lt;/span&gt; xmlHttp;
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;&lt;code&gt;Step 3.&lt;/code&gt; Make asynchronous HTTP request using the XMLHttpRequest object&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;process&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {
    &lt;span class=&quot;hljs-comment&quot;&gt;// proceed only if the xmlHttp object isn&apos;t busy&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (xmlHttp.&lt;span class=&quot;hljs-property&quot;&gt;readyState&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt; || xmlHttp.&lt;span class=&quot;hljs-property&quot;&gt;readyState&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;0&lt;/span&gt;) {
        &lt;span class=&quot;hljs-comment&quot;&gt;// retrieve the name typed by the user on the form&lt;/span&gt;
        item = &lt;span class=&quot;hljs-built_in&quot;&gt;encodeURIComponent&lt;/span&gt;(&lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;getElementById&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;input_item&quot;&lt;/span&gt;).&lt;span class=&quot;hljs-property&quot;&gt;value&lt;/span&gt;);
        &lt;span class=&quot;hljs-comment&quot;&gt;// execute the your_file.php page from the server&lt;/span&gt;
        xmlHttp.&lt;span class=&quot;hljs-title function_&quot;&gt;open&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;GET&quot;&lt;/span&gt;, &lt;span class=&quot;hljs-string&quot;&gt;&quot;your_file.php?item=&quot;&lt;/span&gt; + item, &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;);
        &lt;span class=&quot;hljs-comment&quot;&gt;// define the method to handle server responses&lt;/span&gt;
        xmlHttp.&lt;span class=&quot;hljs-property&quot;&gt;onreadystatechange&lt;/span&gt; = handleServerResponse;
        &lt;span class=&quot;hljs-comment&quot;&gt;// make the server request&lt;/span&gt;
        xmlHttp.&lt;span class=&quot;hljs-title function_&quot;&gt;send&lt;/span&gt;(&lt;span class=&quot;hljs-literal&quot;&gt;null&lt;/span&gt;);
    }
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;&lt;code&gt;Step 4.&lt;/code&gt; Executed automatically when a message is received from the server&lt;/p&gt;

&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;handleServerResponse&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;&lt;/span&gt;) {

    &lt;span class=&quot;hljs-comment&quot;&gt;// move forward only if the transaction has completed&lt;/span&gt;
    &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (xmlHttp.&lt;span class=&quot;hljs-property&quot;&gt;readyState&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;4&lt;/span&gt;) {
        &lt;span class=&quot;hljs-comment&quot;&gt;// status of 200 indicates the transaction completed successfully&lt;/span&gt;
        &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (xmlHttp.&lt;span class=&quot;hljs-property&quot;&gt;status&lt;/span&gt; == &lt;span class=&quot;hljs-number&quot;&gt;200&lt;/span&gt;) {
            &lt;span class=&quot;hljs-comment&quot;&gt;// extract the XML retrieved from the server&lt;/span&gt;
            xmlResponse = xmlHttp.&lt;span class=&quot;hljs-property&quot;&gt;responseText&lt;/span&gt;;
            &lt;span class=&quot;hljs-variable language_&quot;&gt;document&lt;/span&gt;.&lt;span class=&quot;hljs-title function_&quot;&gt;getElementById&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;put_response&quot;&lt;/span&gt;).&lt;span class=&quot;hljs-property&quot;&gt;innerHTML&lt;/span&gt; = xmlResponse;
            &lt;span class=&quot;hljs-comment&quot;&gt;// restart sequence&lt;/span&gt;
        }
        &lt;span class=&quot;hljs-comment&quot;&gt;// a HTTP status different than 200 signals an error&lt;/span&gt;
        &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; {
            &lt;span class=&quot;hljs-title function_&quot;&gt;alert&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&quot;There was a problem accessing the server: &quot;&lt;/span&gt; + xmlHttp.&lt;span class=&quot;hljs-property&quot;&gt;statusText&lt;/span&gt;);
        }
    }
}
&lt;/code&gt;&lt;/pre&gt;

