&lt;pre class=&quot;lang-js s-code-block&quot;&gt;&lt;code class=&quot;hljs language-javascript&quot;&gt;&lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; load_process = &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;;
&lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; &lt;span class=&quot;hljs-title function_&quot;&gt;ajaxCall&lt;/span&gt;(&lt;span class=&quot;hljs-params&quot;&gt;param, response&lt;/span&gt;) {

