 &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (param.&lt;span class=&quot;hljs-property&quot;&gt;async&lt;/span&gt; == &lt;span class=&quot;hljs-literal&quot;&gt;false&lt;/span&gt;) {
         load_process = &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;;
