 &lt;span class=&quot;hljs-keyword&quot;&gt;var&lt;/span&gt; xhr;

 xhr = &lt;span class=&quot;hljs-keyword&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;hljs-title class_&quot;&gt;XMLHttpRequest&lt;/span&gt;();

 &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (param.&lt;span class=&quot;hljs-property&quot;&gt;type&lt;/span&gt; != &lt;span class=&quot;hljs-string&quot;&gt;&quot;GET&quot;&lt;/span&gt;) {
