     }
     &lt;span class=&quot;hljs-keyword&quot;&gt;else&lt;/span&gt; &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (param.&lt;span class=&quot;hljs-property&quot;&gt;contentType&lt;/span&gt; != &lt;span class=&quot;hljs-literal&quot;&gt;undefined&lt;/span&gt; || param.&lt;span class=&quot;hljs-property&quot;&gt;contentType&lt;/span&gt; == &lt;span class=&quot;hljs-literal&quot;&gt;true&lt;/span&gt;) {
         xhr.&lt;span class=&quot;hljs-title function_&quot;&gt;setRequestHeader&lt;/span&gt;(&lt;span class=&quot;hljs-string&quot;&gt;&apos;Content-Type&apos;&lt;/span&gt;, param.&lt;span class=&quot;hljs-property&quot;&gt;contentType&lt;/span&gt;);
