 xhr.&lt;span class=&quot;hljs-property&quot;&gt;onprogress&lt;/span&gt; = &lt;span class=&quot;hljs-keyword&quot;&gt;function&lt;/span&gt; (&lt;span class=&quot;hljs-params&quot;&gt;loadTime&lt;/span&gt;) {
