         &lt;span class=&quot;hljs-keyword&quot;&gt;if&lt;/span&gt; (param.&lt;span class=&quot;hljs-property&quot;&gt;dataType&lt;/span&gt; != &lt;span class=&quot;hljs-literal&quot;&gt;undefined&lt;/span&gt; &amp;amp;&amp;amp; param.&lt;span class=&quot;hljs-property&quot;&gt;dataType&lt;/span&gt; == &lt;span class=&quot;hljs-string&quot;&gt;&quot;json&quot;&lt;/span&gt;) {

